INSERT INTO `creature_text` VALUES (73480, 3, 0, 'Будь осторожнее. Гул\'дана нельзя недооценивать. Он силен и коварен.', 12, 0, 100, 1, 0, 1342, 76041, 30000, 30000, 0, 'Проекция верховного мага Кадгара to Player');
INSERT INTO `creature_text` VALUES (73480, 3, 0, 'Три кристалла размещены в этих руинах. Уничтожь их с помощью сердца Фернуса.', 12, 0, 100, 1, 0, 45007, 77123, 104000, 104000, 0, 'Проекция верховного мага Кадгара to Player');
INSERT INTO `creature_text` VALUES (73480, 3, 0, 'Что ж, во всяком случае, мы что-то узнали. Возвращайся в Тром\'вар через портал.', 12, 0, 100, 1, 0, 45006, 81161, 46000, 46000, 0, 'Проекция верховного мага Кадгара to Player');
INSERT INTO `creature_text` VALUES (73483, 0, 0, 'It is not too late, Garrosh. Lay down the mantle of Warchief. We can end this here, now, with no more bloodshed.', 14, 0, 100, 0, 0, 38934, 74698, 0, 0, 0, 'Thrall to Player');
INSERT INTO `creature_text` VALUES (73483, 3, 0, 'I am not powerless! And I am not alone.', 14, 0, 100, 0, 0, 0, 74700, 106000, 106000, 0, 'Тралл to Player');
