INSERT INTO `creature_text` VALUES (73483, 3, 0, 'Then you have forced my hand.  I will correct the mistake I made long ago.  Spirits of the wind, the earth, the water, hear my call!  Come to my aid!', 14, 0, 100, 0, 0, 38935, 74699, 65000, 65000, 0, 'Thrall to Player');
INSERT INTO `creature_text` VALUES (73483, 3, 0, 'Еще не поздно, Гаррош. Сними с себя мантию вождя. Мы можем закончить все здесь и сейчас.', 14, 0, 100, 0, 0, 0, 74698, 3000, 3000, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (73483, 3, 0, 'Значит, ты меня вынуждаешь... Я исправлю ошибку, которую совершил. Духи ветра, земли, воды, услышьте мой зов! Придите ко мне!', 14, 0, 100, 0, 0, 0, 74699, 69000, 69000, 0, 'Тралл to Player');
