INSERT INTO `creature_text` VALUES (73558, 3, 0, 'Мы закрепимся здесь. Проведите разведку. Нужно выманить Гарроша из укрытия.', 14, 0, 100, 1, 0, 38950, 75239, 26000, 26000, 0, 'Король Вариан Ринн to Леди Джайна Праудмур');
