INSERT INTO `creature_text` VALUES (73598, 3, 0, 'Gorehowl.', 12, 0, 100, 5, 0, 38091, 77046, 26000, 26000, 0, 'Lady Jaina Proudmoore to Lor\'themar Theron');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'I\'m warning you, Lor\'themar. The Alliance is besieging the city and we will destroy Hellscream. Your people had best stay out of our way.', 12, 0, 100, 25, 0, 38093, 77048, 45000, 45000, 0, 'Lady Jaina Proudmoore to Lor\'themar Theron');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'News to no one, Regent Lord. King Wrynn\'s fleet is converging on Orgrimmar as we speak.', 12, 0, 100, 5, 0, 38092, 77047, 32000, 32000, 0, 'Lady Jaina Proudmoore to Lor\'themar Theron');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'So Hellscream\'s arrogance unleashed the last of the sha. I am not surprised.', 12, 0, 100, 1, 0, 38090, 77045, 11000, 11000, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'Вперед, герои! В портал! Осада Оргриммара началась!', 12, 0, 100, 1, 0, 38094, 77049, 63000, 63000, 0, 'Леди Джайна Праудмур to Леди Джайна Праудмур');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'Гордыня Гарроша выпустила последнего ша. Я не удивлена.', 12, 0, 100, 1, 0, 0, 77045, 13000, 13000, 0, 'Леди Джайна Праудмур to Player');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'Кровавый Вой.', 12, 0, 100, 5, 0, 0, 77046, 26000, 26000, 0, 'Леди Джайна Праудмур to Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'Предупреждаю, Лор\'темар. Альянс осаждает город, мы уничтожим Адского Крика. Вам и вашим людям лучше не стоять у нас на пути.', 12, 0, 100, 25, 0, 0, 77048, 45000, 45000, 0, 'Леди Джайна Праудмур to Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'Это уже не новость, лорд. Пока мы с вами беседуем, флот короля Ринна уже подходит к Оргриммару.', 12, 0, 100, 5, 0, 0, 0, 32000, 32000, 0, 'Леди Джайна Праудмур to Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'Champions, come with me. It\'s time to settle grievances with our... \"Warchief.\"', 12, 0, 100, 1, 0, 38162, 77044, 62000, 62000, 0, 'Lor\'themar Theron to Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'It is always a pleasure to see you, Lady Proudmoore.', 12, 0, 100, 1, 0, 38161, 77043, 57000, 57000, 0, 'Lor\'themar Theron to Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'Likewise. Sylvanas and I have both sent ships to support Vol\'jin\'s revolution.', 12, 0, 100, 5, 0, 38160, 77042, 39000, 39000, 0, 'Lor\'themar Theron to Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'Look here! He left his weapon behind.', 14, 0, 100, 25, 0, 38158, 77040, 22000, 22000, 0, 'Lor\'themar Theron to Player');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'This means he\'s completely unhinged.', 12, 0, 100, 6, 0, 38159, 77041, 28000, 28000, 0, 'Lor\'themar Theron to Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'Всегда рад встрече с вами, леди Праудмур.', 12, 0, 100, 1, 0, 0, 77043, 58000, 58000, 0, 'Лор\'темар Терон to Леди Джайна Праудмур');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'Гаррош совсем обезумел.', 12, 0, 100, 6, 0, 0, 77041, 28000, 28000, 0, 'Лор\'темар Терон to Леди Джайна Праудмур');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'Герои, за мной. Пора решить все вопросы с нашим... \"вождем\".', 12, 0, 100, 1, 0, 0, 77044, 62000, 62000, 0, 'Лор\'темар Терон to Леди Джайна Праудмур');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'Как и наш. Мы с Сильваной направили корабли для поддержки восстания Вол\'джина.', 12, 0, 100, 5, 0, 0, 77042, 39000, 39000, 0, 'Лор\'темар Терон to Леди Джайна Праудмур');
