INSERT INTO `creature_text` VALUES (73709, 0, 0, 'Garrosh be concealin\' a whole hidden base. Where did his lust for power take him?', 14, 0, 100, 5, 0, 39146, 76615, 0, 0, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (73709, 0, 0, 'Гаррош скрывал под землей целый комплекс. Куда его завела жажда власти?', 14, 0, 100, 5, 0, 0, 76615, 0, 0, 0, 'Вол\'джин');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'Baine, you and your warriors hold this junction. These heroes should forge ahead an\' flush the Warchief out of hiding.', 14, 0, 100, 1, 0, 39147, 76616, 20000, 20000, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'Hah. Darkspear never die.', 14, 0, 100, 1, 0, 39150, 76619, 61000, 61000, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'It\'s gonna be fine, Baine. We gonna find Thrall, gonna kill de Warchief, gonna avenge your father.', 14, 0, 100, 1, 0, 39149, 76618, 48000, 48000, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'People be dyin\' up above. Da city is in chaos. I\'m gonna finish it - stop da bloodshed. I\'ll come back for ya.', 14, 0, 100, 1, 0, 39148, 76617, 32000, 32000, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'Бейн, все будет хорошо. Мы найдем Тралла, убьем вождя, отомстим за твоего отца.', 14, 0, 100, 1, 0, 0, 76618, 48000, 48000, 0, 'Вол\'джин');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'Бейн, ты и твои воины нужны тут. Наши герои попробуют выбить вождя из укрытия.', 14, 0, 100, 1, 0, 0, 76616, 20000, 20000, 0, 'Вол\'джин');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'Город погрузился в хаос. Повсюду смерть. Я должен остановить эту резню. Затем я вернусь к тебе.', 14, 0, 100, 1, 0, 0, 76617, 32000, 32000, 0, 'Вол\'джин');
INSERT INTO `creature_text` VALUES (73709, 3, 0, 'Хм. Черное Копье не убьешь.', 14, 0, 100, 0, 0, 0, 76619, 61000, 61000, 0, 'Вол\'джин');
INSERT INTO `creature_text` VALUES (73710, 3, 0, '...very well.', 14, 0, 100, 1, 0, 38260, 76630, 45000, 45000, 0, 'Baine Bloodhoof to Vol\'jin');
INSERT INTO `creature_text` VALUES (73710, 3, 0, 'Earthmother protect you, Vol\'jin.', 14, 0, 100, 1, 0, 38261, 76631, 58000, 58000, 0, 'Baine Bloodhoof to Vol\'jin');
INSERT INTO `creature_text` VALUES (73710, 3, 0, 'We will soon find out. He is cornered like a rat down one of these corridors.', 14, 0, 100, 1, 0, 38257, 76627, 8000, 8000, 0, 'Baine Bloodhoof');
INSERT INTO `creature_text` VALUES (73710, 3, 0, 'What about you?', 14, 0, 100, 6, 0, 38259, 76629, 31000, 31000, 0, 'Baine Bloodhoof to Vol\'jin');
INSERT INTO `creature_text` VALUES (73710, 3, 0, 'What should we do next?', 14, 0, 100, 6, 0, 38258, 76628, 17000, 17000, 0, 'Baine Bloodhoof to Vol\'jin');
