INSERT INTO `creature_text` VALUES (73710, 3, 0, 'М-м. Хорошо…', 14, 0, 100, 1, 0, 0, 76630, 44000, 44000, 0, 'Бейн Кровавое Копыто to Вол\'джин');
INSERT INTO `creature_text` VALUES (73710, 3, 0, 'Скоро мы это узнаем. Гаррош прячется как крыса в одном из этих коридоров.', 14, 0, 100, 1, 0, 0, 76627, 8000, 8000, 0, 'Бейн Кровавое Копыто');
