INSERT INTO `creature_text` VALUES (73714, 3, 0, 'I will offer all the help I can, $n. Just promise to decorate your road to Garrosh with many Kor\'kron corpses.', 12, 0, 100, 1, 0, 0, 76732, 23000, 23000, 0, 'Темный мастер Кирин to Player');
