INSERT INTO `creature_text` VALUES (73715, 3, 0, 'I\'ve got the brains, you\'ve got the muscle. Together, we\'ll make beautiful carnage.', 12, 0, 100, 396, 0, 0, 76733, 14000, 14000, 0, 'Rivett Clutchpop to Player');
INSERT INTO `creature_text` VALUES (73715, 3, 0, 'У меня есть мозги, у тебя есть мышцы. Мы хорошо сработаемся!', 12, 0, 100, 71, 0, 39147, 76733, 19000, 19000, 0, 'Риветт Сталехват to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Ah, more of Vol\'jin\'s revolutionaries. You\'ve made it.', 12, 0, 100, 0, 0, 0, 76677, 1195000, 1195000, 0, 'High Overlord Saurfang to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Ah, Nazgrim. A great leader and a fine warrior. He valued his oath to the Warchief more than his life.', 12, 0, 100, 0, 0, 0, 76680, 1217000, 1217000, 0, 'High Overlord Saurfang to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Damn Hellscream.  His ambition tore our Horde apart.', 12, 0, 100, 274, 0, 0, 76682, 1242000, 1242000, 0, 'High Overlord Saurfang to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Go on, find Thrall.  Finish this.  I will live.', 12, 0, 100, 1, 0, 0, 76683, 1250000, 1250000, 0, 'High Overlord Saurfang to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Have you found Thrall? I am badly hurt, he insisted on going ahead alone.', 12, 0, 100, 0, 0, 0, 76678, 1201000, 1201000, 0, 'High Overlord Saurfang to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'I tried to tell him ... that Hellscream had betrayed us ... forgot about responsibility for his people. But Nazgrim was ... too loyal ... too proud.', 12, 0, 100, 0, 0, 0, 76681, 517000, 517000, 139221, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'I tried to tell him... to tell him that Hellscream betrayed us, cast aside a Warchief\'s responsibility to his own people.  But Nazgrim... too loyal... too proud.', 12, 0, 100, 0, 0, 0, 76681, 1228000, 1228000, 0, 'High Overlord Saurfang to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Tell me, how goes the battle up above? What of Nazgrim? Speak to me!', 12, 0, 100, 0, 0, 0, 76679, 1210000, 1210000, 0, 'High Overlord Saurfang to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'А, Назгрим. Великий лидер и отличный воин. Для него клятва вождю была дороже жизни.', 12, 0, 100, 0, 0, 0, 76680, 507000, 507000, 139221, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'А, новые повстанцы Вол\'джина. У вас получилось.', 12, 0, 100, 0, 0, 0, 76677, 485000, 485000, 0, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Амбиции Адского Крика развалили нашу Орду.', 12, 0, 100, 274, 0, 0, 76682, 532000, 532000, 139221, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Вы нашли Тралла? Я сильно ранен, он настоял, что дальше пойдет один.', 12, 0, 100, 0, 0, 0, 76678, 491000, 491000, 139221, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Не останавливайтесь. Найдите Тралла. Я буду жить.', 12, 0, 100, 1, 0, 0, 76683, 539000, 539000, 139221, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Скажите, как битва наверху? Что с Назгримом? Отвечайте!', 12, 0, 100, 0, 0, 0, 76679, 499000, 499000, 139221, 'Верховный правитель Саурфанг to Player');
