INSERT INTO `creature_text` VALUES (74585, 4, 0, 'Ну же, глупцы! Попробуйте напасть на нас!', 12, 0, 100, 0, 0, 0, 77644, 12000, 12000, 0, 'Седовласый ветеран из клана Северного Волка to Player');
INSERT INTO `creature_text` VALUES (74593, 0, 0, 'Клан Веселого Черепа! Уходим отсюда, пока нас не обнаружили.', 12, 0, 100, 0, 0, 44743, 78253, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (74593, 1, 0, 'Дорогой, подумай еще. Добром это не кончится.', 12, 0, 100, 0, 0, 44744, 85302, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (74594, 0, 0, 'Может, они и безумны, но никто лучше них не знает эти земли. Их знания могут нам пригодиться.', 12, 0, 100, 0, 0, 45265, 78186, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (74594, 1, 0, 'Я вышел на охоту за Железной Ордой. Я могу уничтожить то, что осталось от твоей деревни или… мы можем помочь друг другу.', 12, 0, 100, 0, 0, 45266, 78393, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (74594, 3, 0, 'Будь осторожнее. Если слухи верны, тебе грозит смертельная опасность.', 12, 0, 100, 0, 0, 45263, 85998, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (74594, 4, 0, 'Тебе удалось добыть бесценный артефакт. Я ценю твое мужество. Лок\'тар.', 12, 0, 100, 0, 0, 45264, 85999, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (74606, 0, 0, 'Могучий Дуротан спустился со своей горы, но не прихватил с собой армию. Что, жить надоело?', 12, 0, 100, 0, 0, 46175, 78415, 0, 0, 0, 'Каз Визгунья to Player');
INSERT INTO `creature_text` VALUES (74611, 0, 0, 'Если мы не продолжим убивать этих горенов, глядишь, они скоро и вообще забудут, кто мы такие.', 12, 0, 100, 0, 0, 0, 78166, 0, 0, 0, 'Ногорвач to Player');
INSERT INTO `creature_text` VALUES (74631, 0, 0, 'You found the armor, no friend in reach.', 12, 0, 100, 0, 0, 0, 77342, 0, 0, 0, 'Phlox to Player');
INSERT INTO `creature_text` VALUES (74631, 1, 0, 'You find more clues, at the beach.', 12, 0, 100, 0, 0, 0, 77679, 0, 0, 0, 'Phlox to Player');
INSERT INTO `creature_text` VALUES (74632, 0, 0, 'The void... HUNGERS!', 14, 0, 100, 0, 0, 44451, 77695, 0, 0, 0, 'Arkeddon to Player');
INSERT INTO `creature_text` VALUES (74651, 0, 0, 'Ищите их командира, Гарграка!', 14, 1, 100, 0, 0, 41811, 79324, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74651, 1, 0, 'На юг, мои братья и сестры!', 14, 1, 100, 0, 0, 41809, 79322, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74651, 2, 0, 'Спускайся, «Железный Волк». Я покажу, как сражаются настоящие волки.', 14, 1, 100, 0, 0, 41764, 79326, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74651, 4, 0, 'Подкрепления с востока!', 14, 1, 100, 22, 0, 41807, 79320, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74651, 5, 0, 'Они могут нападать, только когда часть клана отсутствует! Убейте этих трусов!', 14, 1, 100, 22, 0, 41810, 79323, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74651, 6, 0, 'Защищайте Пруд Видений! Не дайте убить предсказателя!', 14, 1, 100, 0, 0, 41814, 79327, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74651, 7, 0, 'Иди на северо-запад! Поспеши, спаси Дрек\'Тара!', 12, 0, 100, 5, 0, 41815, 79193, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (74651, 8, 0, 'Watch for their warleader, Gargrak.', 14, 1, 100, 0, 0, 41811, 79324, 0, 0, 0, 'Draka to 0');
INSERT INTO `creature_text` VALUES (74651, 9, 0, 'To the south, now my brothers and sisters.', 14, 1, 100, 0, 0, 41809, 79322, 0, 0, 0, 'Draka to 0');
INSERT INTO `creature_text` VALUES (74651, 10, 0, 'Watch for Thunderlord from the ledge to the north.', 14, 1, 100, 0, 0, 41808, 79321, 0, 0, 0, 'Draka to 0');
INSERT INTO `creature_text` VALUES (74651, 11, 0, 'Follow the path northwest. Hurry, that monster will kill Drek\'Thar!', 12, 0, 100, 5, 0, 41815, 79193, 0, 0, 0, 'Draka to Player');
INSERT INTO `creature_text` VALUES (74651, 15, 0, 'Ожидайте Громоборцев на северной гряде!', 14, 1, 100, 0, 0, 40098, 79321, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74651, 23, 0, 'Дуротан будет нами гордиться.', 14, 1, 100, 0, 0, 41812, 79325, 0, 0, 0, 'Дрека to 0');
INSERT INTO `creature_text` VALUES (74652, 3, 0, 'Focus on the kill.', 14, 0, 100, 0, 0, 0, 78083, 171250, 171250, 0, 'Razorfen Thornbolt');
INSERT INTO `creature_text` VALUES (74653, 0, 0, 'А это ты предвидел, слепой глупец? Ха-ха!', 14, 1, 100, 0, 0, 41768, 79316, 0, 0, 0, 'Военный вождь Гарграк to Player');
INSERT INTO `creature_text` VALUES (74653, 1, 0, 'ЧТО?!', 14, 0, 100, 0, 0, 41769, 125725, 0, 0, 0, 'Военный вождь Гарграк to 0');
INSERT INTO `creature_text` VALUES (74653, 2, 0, 'Громоборцы… Я подвел вас.', 14, 1, 100, 0, 0, 41774, 77848, 0, 0, 0, 'Военный вождь Гарграк to Player');
INSERT INTO `creature_text` VALUES (74697, 0, 0, 'Ай! Получишь сдачи!', 12, 0, 100, 0, 0, 0, 76593, 0, 0, 0, 'Геомант из клана Камнерогов to Геомант из клана Камнерогов');
INSERT INTO `creature_text` VALUES (74697, 0, 0, 'Проваливай, это мое!', 12, 0, 100, 0, 0, 0, 76595, 0, 0, 0, 'Геомант из клана Камнерогов to Геомант из клана Камнерогов');
INSERT INTO `creature_text` VALUES (74697, 0, 0, 'Эй, осторожнее!', 12, 0, 100, 0, 0, 0, 111955, 0, 0, 0, 'Геомант из клана Камнерогов to Огрон из клана Камнерогов');
INSERT INTO `creature_text` VALUES (74697, 4, 0, 'Хватит драться, дубина!', 12, 0, 100, 0, 0, 0, 76594, 7000, 7000, 147117, 'Геомант из клана Камнерогов to Геомант из клана Камнерогов');
INSERT INTO `creature_text` VALUES (74700, 4, 0, 'The tip of the Thunderlord spear, no greater honor.', 12, 1, 100, 0, 0, 40360, 79300, 336750, 336750, 0, 'Gronnstalker Korhol');
INSERT INTO `creature_text` VALUES (74700, 4, 0, 'Нет большей чести, чем смерть от копья Громоборца.', 12, 1, 100, 0, 0, 0, 79300, 7000, 7000, 0, 'Охотник на гроннов Корхол');
INSERT INTO `creature_text` VALUES (74702, 4, 0, 'Смерть жалкому клану Северного Волка.', 12, 1, 100, 0, 0, 0, 79302, 13105, 13105, 0, 'Убийца великанов Жакта');
INSERT INTO `creature_text` VALUES (74708, 4, 0, 'Me and Thunderhoof are going to crush all of you.', 12, 1, 100, 0, 0, 41808, 79301, 9157, 9157, 0, 'Beastmaster Trokar');
INSERT INTO `creature_text` VALUES (74708, 4, 0, 'Мы с Громовым Копытом сокрушим всех вас.', 12, 1, 100, 0, 0, 40360, 79301, 19000, 19000, 0, 'Повелитель зверей Трокар');
INSERT INTO `creature_text` VALUES (74715, 3, 0, 'ENOUGH. Play time is over. I will deal with you myself.', 14, 0, 100, 0, 0, 43657, 82586, 445469, 445469, 0, 'Commander Vorka to Player');
INSERT INTO `creature_text` VALUES (74715, 3, 0, 'Iron Fleet! Darken the skies!', 14, 0, 100, 0, 0, 43656, 81655, 278563, 278563, 0, 'Commander Vorka to Player');
INSERT INTO `creature_text` VALUES (74715, 3, 0, 'Witness the TRUE power of the Iron Horde!', 14, 0, 100, 11, 0, 43658, 82585, 451516, 451516, 0, 'Commander Vorka to Player');
INSERT INTO `creature_text` VALUES (74715, 3, 0, 'Железный флот! Затмите небеса!', 14, 0, 100, 0, 0, 43656, 81655, 84406, 84406, 0, 'Командир Ворка to Player');
INSERT INTO `creature_text` VALUES (74715, 3, 0, 'Узрите истинную силу Железной Орды!', 14, 0, 100, 11, 0, 43658, 78271, 198453, 198453, 0, 'Командир Ворка to Player');
INSERT INTO `creature_text` VALUES (74715, 3, 0, 'Хватит! Игры закончились. Я сам разберусь с вами.', 14, 0, 100, 0, 0, 43657, 81973, 191000, 191000, 0, 'Командир Ворка to Player');
INSERT INTO `creature_text` VALUES (74715, 4, 0, '%s приземляется. В атаку!', 41, 0, 100, 0, 0, 43660, 82187, 29000, 29000, 151694, 'Командир Ворка to Player');
INSERT INTO `creature_text` VALUES (74715, 4, 0, 'Soldiers - Come to my aid!', 14, 0, 100, 0, 0, 43660, 78285, 81797, 81797, 151661, 'Commander Vorka');
INSERT INTO `creature_text` VALUES (74715, 4, 0, 'The skies darken with iron!', 14, 0, 100, 0, 0, 43659, 78272, 59844, 59844, 150274, 'Commander Vorka');
INSERT INTO `creature_text` VALUES (74715, 4, 0, 'Железо затмит небо!', 14, 0, 100, 0, 0, 43659, 78272, 20610, 20610, 150274, 'Командир Ворка');
INSERT INTO `creature_text` VALUES (74715, 4, 0, 'Солдаты, на помощь!', 14, 0, 100, 0, 0, 43660, 78285, 28125, 28125, 151659, 'Командир Ворка');
INSERT INTO `creature_text` VALUES (74720, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 3000, 3000, 1604, 'Мурлок из племени Мутной Воды');
INSERT INTO `creature_text` VALUES (74721, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 10000, 10000, 0, 'Желчный плавник из племени Мутной Воды');
INSERT INTO `creature_text` VALUES (74728, 0, 0, 'Вы опоздали, герои. Призыв Аку\'мая уже завершен.', 14, 0, 100, 0, 0, 45759, 78443, 0, 0, 0, 'Сумеречный владыка Балтиэль to Player');
INSERT INTO `creature_text` VALUES (74728, 1, 0, 'I want you to know before you die: Aku\'mai will swallow this world!', 14, 0, 100, 0, 0, 45757, 78445, 0, 0, 0, 'Сумеречный владыка Балтиэль to Player');
INSERT INTO `creature_text` VALUES (74728, 1, 0, 'Увы, вы не сможете вживую лицезреть его величие. Прикончить их!', 14, 0, 100, 0, 0, 45760, 78444, 0, 0, 0, 'Сумеречный владыка Балтиэль to Player');
INSERT INTO `creature_text` VALUES (74728, 2, 0, 'Бесполезно, глупцы. Аку\'май восстанет!', 14, 0, 100, 0, 0, 45758, 78442, 0, 0, 0, 'Сумеречный владыка Балтиэль to Player');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'Excellent. Keep them away. I\'ve almost got it!', 12, 0, 100, 0, 0, 0, 84222, 110344, 110344, 0, 'Romuul to Embaari Defense Crystal Event Controller Bunny');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'Good work so far! The crystal conduits are energizing. Just a few minutes longer...', 12, 0, 100, 0, 0, 0, 84221, 80422, 80422, 0, 'Romuul to Embaari Defense Crystal Event Controller Bunny');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'It\'s done! Not bad, stranger. Take the spoils from that beast if you wish. We should be safe, now.', 12, 0, 100, 0, 0, 0, 84223, 159375, 159375, 0, 'Romuul to Player');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'Watch out! Here they come!', 12, 0, 100, 0, 0, 0, 84224, 40219, 40219, 0, 'Romuul to Player');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'Все! Ты неплохо сражаешься, $Gнезнакомец:незнакомка;. Если хочешь, можешь забрать трофеи с этой твари. Теперь мы в безопасности.', 12, 0, 100, 0, 0, 0, 84223, 100593, 100593, 0, 'Ромуул to Player');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'Пока неплохо! Кристаллические проводники наполняются энергией. Еще пару минут...', 12, 0, 100, 0, 0, 0, 84221, 43406, 43406, 0, 'Ромуул to Embaari Defense Crystal Event Controller Bunny');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'Прекрасно. Не давай им подойти. Почти готово!', 12, 0, 100, 0, 0, 0, 84222, 63765, 63765, 0, 'Ромуул to Embaari Defense Crystal Event Controller Bunny');
INSERT INTO `creature_text` VALUES (74741, 3, 0, 'Смотри! Вот и они!', 12, 0, 100, 0, 0, 0, 84224, 14000, 14000, 0, 'Ромуул to Player');
INSERT INTO `creature_text` VALUES (74744, 3, 0, 'Они залезли на скалы! Зовите воздаятелей! Кто-нибудь, на помощь!', 12, 0, 100, 5, 0, 0, 77753, 1000, 1000, 0, 'Телуунский астролог to Player');
INSERT INTO `creature_text` VALUES (74749, 3, 0, 'ЧТО? Я тебя помню.', 14, 0, 100, 25, 0, 45342, 77126, 7000, 7000, 149085, 'Гул\'дан');
INSERT INTO `creature_text` VALUES (74749, 4, 0, 'I would never go down to a fight with you ...', 14, 0, 100, 1, 0, 45343, 77127, 11000, 11000, 0, 'Гул\'дан');
INSERT INTO `creature_text` VALUES (74749, 4, 0, 'Гизельда, красавица моя, настал твой час.', 14, 0, 100, 0, 0, 1342, 77130, 18000, 18000, 0, 'Гул\'дан');
INSERT INTO `creature_text` VALUES (74749, 4, 0, 'Не так быстро.', 14, 0, 100, 1, 0, 45318, 76042, 6000, 6000, 0, 'Гул\'дан');
INSERT INTO `creature_text` VALUES (74749, 4, 0, 'Неважно. Скоро я создам тысячи воинов для Пылающего Легиона.', 14, 0, 100, 1, 0, 45321, 76045, 65000, 65000, 0, 'Гул\'дан to Гизельда Прекрасная');
INSERT INTO `creature_text` VALUES (74749, 4, 0, 'Ну, развлекайся.', 14, 0, 100, 396, 0, 45320, 75991, 33000, 33000, 0, 'Гул\'дан to Гизельда Прекрасная');
INSERT INTO `creature_text` VALUES (74749, 4, 0, 'Передавай привет Кадгару. Я приготовил для него подарок.', 14, 0, 100, 25, 0, 45322, 75974, 74000, 74000, 0, 'Гул\'дан to Гизельда Прекрасная');
INSERT INTO `creature_text` VALUES (74778, 0, 0, 'Your blood will grant me the powers of the Dark Star. To Shaz\'gul with you!', 12, 0, 100, 0, 0, 43699, 77863, 0, 0, 0, 'Ner\'zhul to Player');
INSERT INTO `creature_text` VALUES (74778, 1, 0, 'Tell your prophet that his era is over. Darkness dawns!', 12, 0, 100, 5, 0, 43700, 78838, 0, 0, 0, 'Ner\'zhul to Player');
INSERT INTO `creature_text` VALUES (74778, 2, 0, '%s\'s laugh echoes ominously in your mind.', 16, 0, 100, 5, 0, 43700, 77974, 0, 0, 0, 'Ner\'zhul to Player');
INSERT INTO `creature_text` VALUES (74787, 0, 0, 'Стражи! Хватит зевать, пора убивать.', 14, 0, 100, 54, 0, 41647, 78345, 0, 0, 0, 'Надсмотрщик за рабами Крушто to Огр-маг из клана Кровавого Молота');
INSERT INTO `creature_text` VALUES (74787, 1, 0, '...Мои рабы! Собственность Кровавого Молота!', 14, 0, 100, 53, 0, 41648, 78351, 0, 0, 0, 'Надсмотрщик за рабами Крушто to Порабощенный шахтер');
INSERT INTO `creature_text` VALUES (74787, 2, 0, 'Идите сюда! Я закую вас в цепи.', 14, 0, 100, 0, 0, 41645, 78047, 0, 0, 0, 'Надсмотрщик за рабами Крушто to Player');
INSERT INTO `creature_text` VALUES (74787, 3, 0, 'Хватайте этого!', 14, 0, 100, 0, 0, 41652, 77969, 0, 0, 0, 'Надсмотрщик за рабами Крушто to 0');
INSERT INTO `creature_text` VALUES (74787, 4, 0, 'Рабы! Бейтесь или умрите!', 14, 0, 100, 0, 0, 41654, 77992, 0, 0, 0, 'Надсмотрщик за рабами Крушто to 0');
INSERT INTO `creature_text` VALUES (74787, 5, 0, 'У-у… мы вас всех убьем…', 14, 0, 100, 0, 0, 41646, 78570, 0, 0, 0, 'Надсмотрщик за рабами Крушто to Player');
INSERT INTO `creature_text` VALUES (74790, 0, 0, 'Сломать вас будет удовольствием.', 14, 0, 100, 0, 0, 42068, 83626, 0, 0, 0, 'Гугрокк to Player');
INSERT INTO `creature_text` VALUES (74790, 1, 0, 'Сначала сломлю ваш дух, потом продам в рабство.', 14, 0, 100, 0, 0, 42065, 83627, 0, 0, 0, 'Гугрокк to Player');
INSERT INTO `creature_text` VALUES (74790, 2, 0, 'Горите!', 14, 0, 100, 0, 0, 42071, 133419, 0, 0, 0, 'Гугрокк to 0');
INSERT INTO `creature_text` VALUES (74790, 3, 0, 'Стихии повинуются мне!', 14, 0, 100, 0, 0, 42073, 83630, 0, 0, 0, 'Гугрокк to 0');
INSERT INTO `creature_text` VALUES (74790, 4, 0, 'Вам… не видать... свободы…', 14, 0, 100, 0, 0, 42067, 83634, 0, 0, 0, 'Гугрокк to Player');
INSERT INTO `creature_text` VALUES (74790, 5, 0, '|TInterface\\Icons\\spell_fire_elementaldevastation.blp:20|t |cFFF00000|Hspell:150678|h[Огненные Недра]|h|r заставляют |3-3(%s) вызвать |cFFF00000|Hspell:150682|h[Лавовый залп]|h|r!', 41, 0, 100, 0, 0, 42072, 77894, 0, 0, 0, 'Гугрокк to 0');
INSERT INTO `creature_text` VALUES (74790, 6, 0, 'Вы будете рабами лавы!', 14, 0, 100, 0, 0, 42072, 83629, 0, 0, 0, 'Гугрокк to 0');
INSERT INTO `creature_text` VALUES (74848, 3, 0, 'Well done. When the Iron Horde takes the draenei temple by sea, your clan shall be honored within our ranks.', 12, 0, 100, 6, 0, 0, 76782, 102750, 102750, 0, 'Arnokk the Burner to Player');
INSERT INTO `creature_text` VALUES (74848, 3, 0, 'Отлично. Когда Железная Орда возьмет дренейский город с моря, твои орки удостоятся особых почестей.', 12, 0, 100, 6, 0, 0, 76782, 13000, 13000, 0, 'Арнокк Сжигатель to Player');
INSERT INTO `creature_text` VALUES (74875, 0, 0, 'Амненнар восстанет из мертвых!', 14, 0, 100, 0, 0, 46219, 78199, 0, 0, 0, 'Вестник смерти Черношип');
INSERT INTO `creature_text` VALUES (74875, 1, 0, 'Мои друзья займутся вами.', 14, 0, 100, 0, 0, 46224, 78197, 0, 0, 0, 'Вестник смерти Черношип');
INSERT INTO `creature_text` VALUES (74875, 2, 0, 'Слишком... поздно. С моей смертью Амненнар переродился…', 14, 0, 100, 0, 0, 46220, 77870, 0, 0, 0, 'Вестник смерти Черношип');
INSERT INTO `creature_text` VALUES (74877, 0, 0, 'Why? What is the use of saving them? Only death awaits us all in the end.', 12, 0, 100, 6, 0, 45622, 81488, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 1, 0, 'I will live to protect the righteous, the helpless, and the hopeless. You are a wise teacher, $n.', 12, 0, 100, 1, 0, 45624, 81929, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 2, 0, 'A hero? Hardly! I am just an initiate, and not a very good one at that.', 12, 0, 100, 1, 0, 45614, 81490, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 3, 0, 'You are right, $n. My choices do matter. I choose... to fight!', 12, 0, 100, 1, 0, 45617, 81496, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 4, 0, 'Is it true? Good doesn\'t always triumph over evil.', 12, 0, 100, 1, 0, 45618, 81498, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 5, 0, 'You are right, $n. I will not bow quietly to these enemies. I must fight!', 12, 0, 100, 1, 0, 45621, 81504, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 6, 0, 'Let us do this.', 12, 0, 100, 1, 0, 45625, 81930, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 7, 0, 'Сейчас не подходящее время для шуток. Но в твоих словах есть здравый смысл. Я должна быть храброй!', 12, 0, 100, 1, 0, 45620, 81503, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 8, 0, 'You are truly wise, $n. I must believe in myself, for I carry the Light within me.', 12, 0, 100, 1, 0, 45619, 81502, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 9, 0, 'Спасибо тебе. Отныне я не позволю страхам сбить меня с пути.', 12, 0, 100, 1, 0, 45616, 81494, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 10, 0, 'I see, $n. You have never backed down from a challenge. I must strive to do the same.', 12, 0, 100, 1, 0, 45615, 81492, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74877, 11, 0, 'You\'re absolutely right, $n. Samaara and the others are depending on us. For them, I must survive!', 12, 0, 100, 1, 0, 45623, 81928, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (74888, 3, 0, 'Вместе мы можем помешать Вестнику смерти Черношипу. Но нужно спешить. Ритуал почти завершен!', 14, 0, 100, 0, 0, 43602, 78063, 25854, 25854, 0, 'Користраза to Player');
INSERT INTO `creature_text` VALUES (74900, 1, 0, 'Мощь земли... даруй мне силу!', 14, 0, 100, 0, 0, 41844, 77896, 0, 0, 0, 'Ваятель лабиринтов Тукаар');
INSERT INTO `creature_text` VALUES (74900, 2, 0, 'Мое тело возвращается... в лабиринты.', 14, 0, 100, 0, 0, 41845, 77897, 0, 0, 0, 'Ваятель лабиринтов Тукаар to Player');
INSERT INTO `creature_text` VALUES (74900, 3, 0, 'Будьте начеку... камни предвещают беду.', 14, 0, 100, 0, 0, 41846, 80455, 26160, 26160, 0, 'Ваятель лабиринтов Тукаар to Player');
INSERT INTO `creature_text` VALUES (74927, 7, 0, '|TInterface\\Icons\\spell_fire_selfdestruct.blp:20|t %s: |cFFF00000|Hspell:150734|h[Необузданное Пламя]|h|r достигает максимальной силы!', 41, 0, 100, 0, 0, 0, 77911, 0, 0, 0, 'Нестабильный шлаковый элементаль to Нестабильный шлаковый элементаль');
INSERT INTO `creature_text` VALUES (74948, 1, 0, 'Вы умрете во имя Агамаггана!', 14, 0, 100, 0, 0, 41659, 77916, 0, 0, 0, 'Ругуг to Player');
INSERT INTO `creature_text` VALUES (74948, 2, 0, 'I am one with the earth.', 14, 0, 100, 0, 0, 41664, 78087, 0, 0, 0, 'Ругуг to Player');
INSERT INTO `creature_text` VALUES (74948, 3, 0, 'Кристаллы говорили со мной. Мы победим.', 14, 0, 100, 0, 0, 41661, 77918, 181359, 181359, 0, 'Ругуг to Player');
INSERT INTO `creature_text` VALUES (74948, 4, 0, 'Кровь Агамаггана! Сожги их!', 14, 0, 100, 0, 0, 41665, 77920, 12300, 46209, 0, 'Ругуг to Player');
INSERT INTO `creature_text` VALUES (74977, 3, 0, 'Фомиевая руда крепче закаленной стали. Чтобы разбить ее, тебе потребуется моя кирка, сделанная из зуба горена.', 12, 0, 100, 0, 0, 0, 79482, 23000, 23000, 0, 'Посвященная На\'Шра to Player');
INSERT INTO `creature_text` VALUES (74979, 0, 0, 'Спасибо вам, герои!', 12, 0, 100, 4, 0, 0, 77939, 0, 0, 0, 'Испуганная пленница to Player');
INSERT INTO `creature_text` VALUES (74979, 1, 0, 'They wanted to feed us Aku\'maia. There are still prisoners left in the dungeon. They need help too!', 12, 0, 100, 0, 0, 43550, 77940, 0, 0, 0, 'Испуганная пленница to Player');
INSERT INTO `creature_text` VALUES (74979, 1, 0, 'Они хотели скормить нас Аку\'маю. В подземелье еще остались пленники. Им тоже нужна помощь!', 12, 0, 100, 0, 0, 43550, 77940, 0, 0, 0, 'Испуганная пленница to Player');
INSERT INTO `creature_text` VALUES (74979, 2, 0, 'No! Ah-ah-ah-ah-ah-ah-ah...', 12, 0, 100, 0, 0, 0, 77948, 0, 0, 0, 'Испуганная пленница to Палач Мясодел');
INSERT INTO `creature_text` VALUES (74979, 2, 0, 'Нет! А-а-а-а-а-а...', 12, 0, 100, 0, 0, 0, 77948, 0, 0, 0, 'Испуганная пленница to Палач Мясодел');
INSERT INTO `creature_text` VALUES (74980, 0, 0, 'Глупцы! Вам оказали честь стать пищей Аку\'мая, разрушителя Азерота!', 12, 0, 100, 397, 0, 45105, 77927, 0, 0, 0, 'Сумеречный лекарь бурь to Player');
INSERT INTO `creature_text` VALUES (74980, 2, 0, 'И тебя поглотят со временем.', 12, 0, 100, 0, 0, 0, 77723, 0, 0, 0, 'Сумеречный лекарь бурь to Player');
INSERT INTO `creature_text` VALUES (74988, 0, 0, 'Strangers? Take the prisoners away! Their flesh belongs to Lord Batiel.', 14, 0, 100, 53, 0, 43550, 77941, 0, 0, 0, 'Палач Мясодел to Player');
INSERT INTO `creature_text` VALUES (74988, 0, 0, 'Чужаки? Уводите пленников! Их плоть принадлежит владыке Батиэлю.', 14, 0, 100, 53, 0, 43550, 77941, 0, 0, 0, 'Палач Мясодел to Player');
INSERT INTO `creature_text` VALUES (74988, 1, 0, 'Чего вы ждете, герои? Водичка теплая! Ха-ха-ха-ха-ха!', 14, 0, 100, 0, 0, 43551, 77942, 0, 0, 0, 'Палач Мясодел to Player');
INSERT INTO `creature_text` VALUES (74988, 2, 0, 'The strangers are still alive?! Get them! The ritual cannot be interrupted!', 14, 0, 100, 5, 0, 43552, 78100, 0, 0, 0, 'Палач Мясодел to Player');
INSERT INTO `creature_text` VALUES (74988, 2, 0, 'Чужаки все еще живы?! Схватить их! Ритуал нельзя прерывать!', 14, 0, 100, 5, 0, 43552, 78100, 0, 0, 0, 'Палач Мясодел to Player');
INSERT INTO `creature_text` VALUES (75001, 1, 0, 'Мои вепри затопчут вас!', 14, 0, 100, 0, 0, 41945, 77958, 0, 0, 0, 'Охотник Костяной Бивень to Player');
INSERT INTO `creature_text` VALUES (75001, 2, 0, 'Моя слава не угаснет!', 14, 0, 100, 0, 0, 41946, 78089, 0, 0, 0, 'Охотник Костяной Бивень to Player');
INSERT INTO `creature_text` VALUES (75001, 3, 0, 'Охотники, готовьте ловушки. Я что-то чую. ', 14, 0, 100, 0, 0, 41948, 80456, 74421, 74421, 0, 'Охотник Костяной Бивень to Player');
INSERT INTO `creature_text` VALUES (75001, 4, 0, 'Много стрел, один труп.', 14, 0, 100, 0, 0, 41947, 77957, 18742, 42974, 0, 'Охотник Костяной Бивень to Player');
INSERT INTO `creature_text` VALUES (75001, 4, 0, 'Топчите их!', 14, 0, 100, 0, 0, 41950, 77960, 13552, 13552, 0, 'Охотник Костяной Бивень');
INSERT INTO `creature_text` VALUES (75005, 0, 0, 'Yrel? After the Iron Horde took you, I feared the worst!', 12, 0, 100, 3, 0, 46911, 82552, 0, 0, 0, 'Samaara to Player');
INSERT INTO `creature_text` VALUES (75005, 1, 0, 'Ner\'zhul, stop this! What do you want?', 12, 0, 100, 0, 0, 43222, 77991, 0, 0, 0, 'Samaara to Player');
INSERT INTO `creature_text` VALUES (75005, 2, 0, 'Yrel.... noooo!', 12, 0, 100, 0, 0, 43223, 80009, 0, 0, 0, 'Samaara to Player');
INSERT INTO `creature_text` VALUES (75005, 3, 0, 'Ирель, не плачь. Я не выбирала свою судьбу, но ты еще можешь... выбрать.', 12, 0, 100, 0, 0, 43226, 78861, 0, 0, 0, 'Самаара to Player');
INSERT INTO `creature_text` VALUES (75005, 4, 0, 'Буь сильной, малышка. Я буду присматривать за тобой.', 12, 0, 100, 0, 0, 43225, 78862, 0, 0, 0, 'Самаара to Player');
INSERT INTO `creature_text` VALUES (75011, 0, 0, 'Don\'t fight it, Ghamu-ra! Soon you will be in my power, and I will bring your rage down on these curious heroes!', 12, 0, 100, 0, 0, 43726, 77975, 0, 0, 0, 'Сумеречный лекарь бурь to Player');
INSERT INTO `creature_text` VALUES (75011, 0, 0, 'Не сопротивляйся, Гхаму-ра! Скоро ты окажешься в моей власти, и я обрушу твою ярость на этих любопытных героев!', 12, 0, 100, 0, 0, 43726, 77975, 0, 0, 0, 'Сумеречный лекарь бурь to Player');
INSERT INTO `creature_text` VALUES (75011, 1, 0, 'What? It\'s too soon! Nooooooooooooo...', 12, 0, 100, 0, 0, 43725, 77977, 0, 0, 0, 'Сумеречный лекарь бурь to Player');
INSERT INTO `creature_text` VALUES (75011, 1, 0, 'Что? Слишком рано! НЕЕЕЕЕЕееееет…..', 12, 0, 100, 0, 0, 43725, 77977, 0, 0, 0, 'Сумеречный лекарь бурь to Player');
INSERT INTO `creature_text` VALUES (75039, 0, 0, 'Нет, умоляю! Я не хочу так умирать!!!', 12, 0, 100, 0, 0, 43728, 78313, 0, 0, 0, 'Испуганный пленник to Player');
INSERT INTO `creature_text` VALUES (75058, 2, 0, 'Моя смерть придаст силу древним богам...', 12, 0, 100, 0, 0, 0, 77721, 0, 0, 150504, 'Сумеречный аквамант to Player');
INSERT INTO `creature_text` VALUES (75071, 3, 0, 'The might of my ancestors flows through me!', 14, 0, 100, 0, 0, 0, 79384, 1294797, 1294797, 0, 'Mother Om\'ra');
INSERT INTO `creature_text` VALUES (75071, 3, 0, 'Who dares defile our ancient lands?', 14, 0, 100, 0, 0, 0, 79090, 128094, 128094, 0, 'Mother Om\'ra to Shadowmoon Darkcaster');
INSERT INTO `creature_text` VALUES (75071, 3, 0, 'Кто смеет осквернять наши священные земли?', 14, 0, 100, 0, 0, 0, 79090, 111000, 111000, 0, 'Мать Ом\'ра to Темный маг из клана Призрачной Луны');
INSERT INTO `creature_text` VALUES (75071, 4, 0, 'Мощь предков наполняет меня!', 14, 0, 100, 0, 0, 0, 79384, 21000, 21000, 154450, 'Мать Ом\'ра');
INSERT INTO `creature_text` VALUES (75089, 0, 0, 'Грядет железная буря!', 12, 0, 100, 0, 0, 0, 88765, 0, 0, 0, 'Гром\'карский щитоносец to Player');
INSERT INTO `creature_text` VALUES (75091, 0, 0, 'You worms dare to oppose us?', 12, 0, 100, 0, 0, 0, 88766, 0, 0, 0, 'Grom\'kar Grunt to Player');
INSERT INTO `creature_text` VALUES (75091, 1, 0, 'Ничто не остановит Железную Орду!', 12, 0, 100, 0, 0, 0, 88764, 0, 0, 0, 'Гром\'карский рубака to Player');
INSERT INTO `creature_text` VALUES (75091, 2, 0, 'Это для тебя добром не кончится.', 12, 0, 100, 0, 0, 0, 127773, 0, 0, 0, 'Гром\'карский рубака to Player');
INSERT INTO `creature_text` VALUES (75094, 1, 0, 'Ты не входишь в число избранных.', 12, 0, 100, 0, 0, 0, 79035, 0, 0, 0, 'Утрофис to Player');
INSERT INTO `creature_text` VALUES (75114, 3, 0, 'Они убили Костяного Бивня... Надо их задержать!', 14, 0, 100, 0, 0, 41946, 78929, 153333, 153333, 0, 'Ваятельница лабиринтов из племени Иглошкурых to Player');
INSERT INTO `creature_text` VALUES (75127, 0, 0, 'Many leagues of deadly land yet stand between us and the Blackrock.', 12, 0, 100, 0, 0, 44837, 86013, 0, 0, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (75127, 1, 0, 'Gorgrond is no place for armies. This land swallows all who enter. We will be fortunate to even lay eyes upon the Iron Horde base.', 12, 0, 100, 0, 0, 44838, 86014, 0, 0, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (75136, 0, 0, 'I\'m gonna load up me tank and head southwest. I\'ll see if I can\'t get one of these rangari to be a guide.', 12, 0, 100, 25, 0, 0, 83829, 0, 0, 0, 'Thaelin Darkanvil to Player');
INSERT INTO `creature_text` VALUES (75145, 0, 0, 'Все. Мы победили!', 12, 0, 100, 1, 0, 44823, 82589, 0, 0, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (75176, 0, 0, 'I will take them to cover. Meet me outside.', 12, 0, 100, 0, 0, 45189, 78142, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (75186, 0, 0, 'Теперь все зависит от тебя. Я останусь здесь и помогу раненым рабам.', 12, 0, 100, 113, 0, 46361, 79435, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (75188, 0, 0, 'Нет, Га\'нар. Мы окружены и уязвимы. Надо обороняться.', 12, 0, 100, 396, 0, 45196, 78298, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (75188, 1, 0, 'Как безрассудно…', 12, 0, 100, 396, 0, 45197, 78299, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (75188, 2, 0, 'Твои доблесть и верность клану несомненны.', 12, 0, 100, 396, 0, 45198, 78396, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (75188, 3, 0, 'Нам будет непросто удержать цитадель Камнерогов, а лучшие воины ушли с моим безрассудным братом.', 12, 0, 100, 396, 0, 45199, 78397, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (75188, 4, 0, 'Я был впечатлен твоим мастерством во время битвы с Дороггом.', 12, 0, 100, 396, 0, 45200, 78398, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (75188, 5, 0, 'Почту за честь, если ты будешь и дальше сражаться вместе с нами.', 12, 0, 100, 396, 0, 45201, 78399, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (75191, 0, 0, 'Растопка из тебя получше, чем шахтер!', 14, 0, 100, 15, 0, 0, 78484, 0, 0, 0, 'Поработитель из клана Кровавого Молота to Player');
INSERT INTO `creature_text` VALUES (75195, 3, 0, 'А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А-А!', 14, 0, 100, 0, 0, 0, 78485, 239000, 239000, 0, 'Порабощенный шахтер to Поработитель из клана Кровавого Молота');
