INSERT INTO `creature_text` VALUES (75199, 3, 0, 'Побежали! Это наш шанс!', 14, 0, 100, 0, 0, 0, 78208, 104000, 104000, 0, 'Порабощенный шахтер to Страж из клана Кровавого Молота');
INSERT INTO `creature_text` VALUES (75210, 0, 0, 'Мы их наказать!', 14, 0, 100, 0, 0, 0, 78350, 0, 0, 0, 'Страж из клана Кровавого Молота to Огр-маг из клана Кровавого Молота');
INSERT INTO `creature_text` VALUES (75210, 1, 0, 'Моя их остановить!', 14, 0, 100, 0, 0, 0, 78349, 0, 0, 0, 'Страж из клана Кровавого Молота to Огр-маг из клана Кровавого Молота');
INSERT INTO `creature_text` VALUES (75211, 0, 0, 'Мир охватит пламя!', 14, 0, 100, 53, 0, 0, 78167, 0, 0, 0, 'Магматический властелин to 0');
INSERT INTO `creature_text` VALUES (75223, 0, 0, 'Как раз вовремя! У меня уже огры кончаются, убивать некого…', 12, 0, 100, 0, 0, 0, 78194, 0, 0, 0, 'Гол\'кош Секира to Player');
INSERT INTO `creature_text` VALUES (75235, 3, 0, 'Выходи, Боррок!', 14, 0, 100, 0, 0, 0, 78021, 32000, 32000, 0, 'Жрец Червя из клана Жестокого Холода to 72156');
INSERT INTO `creature_text` VALUES (75235, 3, 0, 'Огры из клана Жестокого Холода платят тебе дань! Покажись!', 14, 0, 100, 0, 0, 0, 78025, 13000, 13000, 0, 'Жрец Червя из клана Жестокого Холода to 72156');
INSERT INTO `creature_text` VALUES (75240, 4, 0, 'Хватит драться, дубина!', 12, 0, 100, 0, 0, 0, 76594, 7000, 7000, 147117, 'Объездчик вепрей из клана Камнерогов to Громила из клана Камнерогов');
INSERT INTO `creature_text` VALUES (75256, 0, 0, 'Auchindoun is safe as long as it is kept sealed. We need to retrieve the fragments so my priests can protect them.', 12, 0, 100, 1, 0, 43630, 79850, 0, 0, 0, 'Soulbinder Nyami to Player');
INSERT INTO `creature_text` VALUES (75256, 1, 0, 'Tuulani, go to Telmor and meet with Soulbinder Aedraa. She has retrieved the final fragment and brought it there.', 12, 0, 100, 396, 0, 43633, 80701, 0, 0, 0, 'Soulbinder Nyami to Player');
INSERT INTO `creature_text` VALUES (75283, 1, 0, 'Жалкий земляной червяк!', 12, 0, 100, 0, 0, 0, 91165, 0, 0, 0, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 1, 0, 'Изгнать нарушителей из наших владений!', 12, 0, 100, 0, 0, 0, 91160, 0, 0, 0, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 1, 0, 'Ты – язва на теле мира.', 12, 0, 100, 0, 0, 0, 91164, 0, 0, 0, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 2, 0, 'Не доверяйте изгоям...', 12, 0, 100, 0, 0, 0, 91168, 0, 0, 0, 'Приверженец-крылатый стражник to Player');
INSERT INTO `creature_text` VALUES (75283, 3, 0, 'Да уж. К тому же здесь водятся низшие расы.', 12, 0, 100, 0, 0, 0, 78387, 20000, 20000, 0, 'Приверженец-крылатый стражник to Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 3, 0, 'Ох! Как же я ненавижу пещеры; тьма в них – хоть глаз выколи.', 12, 0, 100, 0, 0, 0, 78386, 1106500, 1106500, 0, 'Приверженец-крылатый стражник to Player');
INSERT INTO `creature_text` VALUES (75283, 3, 0, 'Ты сгоришь с изгоями!', 12, 0, 100, 0, 0, 0, 91166, 52000, 52000, 0, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Pitiful ground dweller!', 12, 0, 100, 0, 0, 0, 91165, 36656, 36656, 0, 'Adherent Wing-Guard');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Всех уничтожить!', 12, 0, 100, 0, 0, 0, 91159, 42000, 42000, 138175, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Вы все сгорите...', 12, 0, 100, 0, 0, 0, 91167, 30000, 30000, 0, 'Приверженец-крылатый стражник to Player');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Вы умрете как изгои!', 12, 0, 100, 0, 0, 0, 91161, 30000, 30000, 138175, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Их проклятье пожрет вас...', 12, 0, 100, 0, 0, 0, 91169, 18828, 18828, 0, 'Приверженец-крылатый стражник to Player');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Низшим нет пощады.', 12, 0, 100, 0, 0, 0, 91162, 18000, 18000, 138175, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Рухмар… не желала такого…', 12, 0, 100, 0, 0, 0, 91170, 4000, 4000, 0, 'Приверженец-крылатый стражник to Player');
INSERT INTO `creature_text` VALUES (75283, 4, 0, 'Это не твоя битва, $gстранник:странница;.', 12, 0, 100, 0, 0, 0, 0, 4000, 4000, 0, 'Приверженец-крылатый стражник');
INSERT INTO `creature_text` VALUES (75286, 0, 0, 'Я буду служить и после смерти!', 12, 0, 100, 0, 0, 0, 78565, 0, 0, 0, 'Сумеречный послушник to Ужас из пучины');
INSERT INTO `creature_text` VALUES (75288, 0, 0, 'I am Iskar. And now that you have become acquainted with the arakkoa, I wonder if you might aid me in subverting them.', 12, 0, 100, 0, 0, 46310, 79026, 0, 0, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'Fearlessly delving into the unknowable. Seeking answers in the dark...', 12, 0, 100, 0, 0, 46309, 79025, 35437, 35437, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'Forgive my deception. I had to appeal to your sympathy in order to play you against Kairyx.', 12, 0, 100, 0, 0, 46306, 78407, 7406, 7406, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'I am Iskar. What do you think, together we can overthrow arakkoa? Now you know what they are capable of.', 12, 0, 100, 0, 0, 46310, 79026, 38000, 38000, 0, 'Темный ведун Искар to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'I half-expected him to vaporize you, but you dispatched him quite handily. An impressive display...', 12, 0, 100, 0, 0, 46307, 78408, 15359, 15359, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'Though... it was your decision to enter the mine that truly piqued my interest. Your desire to reveal the truth behind the attack.', 12, 0, 100, 0, 0, 46308, 78425, 24344, 24344, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'Но больше всего меня удивило твое решение войти в шахту и выяснить, что стояло за этим нападением.', 12, 0, 100, 0, 0, 46308, 78425, 21000, 21000, 0, 'Темный ведун Искар to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'Прости за обман, но мне пришлось воззвать к твоей жалости, чтобы настроить тебя против Кайрикса.', 12, 0, 100, 0, 0, 46306, 78407, 4000, 4000, 0, 'Темный ведун Искар to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'Ты без страха шагаешь в неизвестность в поисках ответов…', 12, 0, 100, 0, 0, 46309, 79025, 32000, 32000, 0, 'Темный ведун Искар to Player');
INSERT INTO `creature_text` VALUES (75288, 3, 0, 'Я боялся, что он разделается с тобой, но тебе удалось легко победить его. Потрясающе!', 12, 0, 100, 0, 0, 46307, 78408, 12000, 12000, 0, 'Темный ведун Искар to Player');
