INSERT INTO `creature_text` VALUES (75290, 3, 0, 'Изгнать нарушителей из наших владений!', 12, 0, 100, 0, 0, 0, 91160, 76000, 76000, 0, 'Штормокрылый приверженец');
INSERT INTO `creature_text` VALUES (75290, 3, 0, 'Очистить комнату! Обыщите каждый угол, проверьте каждую тень! Артефакт не трогать, пока мы не убедимся, что он в безопасности!', 14, 0, 100, 0, 0, 0, 79034, 28000, 28000, 0, 'Штормокрылый приверженец to Player');
