INSERT INTO `creature_text` VALUES (75294, 1, 0, 'А, вот ты где. По запаху чуял, что где-то рядом |3-6($r).', 12, 0, 100, 0, 0, 0, 80684, 0, 0, 157419, 'Ксанатос Осквернитель to Player');
