INSERT INTO `creature_text` VALUES (75392, 3, 0, 'They are far too powerful to attack head-on. Restalaan is most familiar with this area. I believe he has a plan.', 12, 0, 100, 1, 0, 44603, 83780, 10891, 770297, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (75392, 3, 0, 'Мы не можем напасть открыто: они слишком сильны. Ресталаан знает эти места, и у него есть план.', 12, 0, 100, 1, 0, 44603, 83780, 15000, 15000, 0, 'Экзарх Маладаар to Player');
