INSERT INTO `creature_text` VALUES (77075, 0, 0, 'What? Where am I? I am so tired.', 12, 0, 100, 6, 0, 43257, 80648, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (77075, 1, 0, 'Wait a second. It is coming back to me. This is Blackrock Spire, and we are... we are here to vanquish evil and get me some much needed loot.', 12, 0, 100, 0, 0, 43258, 80649, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (77075, 2, 0, 'I remember it all, now. You are my pals, my pals for life. Weird, I thought there were more of you.', 12, 0, 100, 0, 0, 43259, 80650, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (77075, 3, 0, 'No matter. You are the brains, the creators of the perfect plan, and I am the one who will enact that plan with surgical accuracy.', 12, 0, 100, 0, 0, 43260, 80651, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (77075, 4, 0, 'But first, I am hungry. It is like I have not eaten in 8 years. I guess I will just cook this chicken that I have in my bag while you devise our plan of attack.', 12, 0, 100, 0, 0, 43261, 80652, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (77075, 5, 0, 'Alright chums, I\'m back! Let\'s do this! Leeroooooy... Jenkins!', 14, 0, 100, 53, 0, 43253, 80655, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
