INSERT INTO `creature_text` VALUES (77075, 7, 0, 'Now time for a little well deserved rest and relaxation! If you need me, I will be back at your garrison.', 12, 0, 100, 1, 0, 43256, 82627, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (77075, 8, 0, 'Вот и она, моя курочка, завалявшаяся в рюкзаке. А вы не отвлекайтесь, вам еще план придумывать.', 12, 0, 100, 1, 0, 0, 80653, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (77075, 9, 0, 'Шикарный план, ребята, я в восторге. Все так продумано и выверено до мелочей.', 12, 0, 100, 1, 0, 0, 80654, 0, 0, 0, 'Лирой Дженкинс to Лирой Дженкинс');
