INSERT INTO `creature_text` VALUES (77310, 1, 0, 'КТО ТЕБЯ СЮДА ПРИСЛАЛ?! Бутон Смерти. Ну конечно, это Бутон Смерти. БУДЬ ТЫ $gПРОКЛЯТ:ПРОКЛЯТА; !', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Сумасшедший \"Король\" Спореон');
INSERT INTO `creature_text` VALUES (77310, 3, 0, 'WHO SENT YOU HERE?! Deathbloom. Surely it was Deathbloom. CURSE YOU!', 14, 0, 100, 0, 0, 0, 80176, 245016, 245016, 0, 'Mad \"King\" Sporeon');
INSERT INTO `creature_text` VALUES (77312, 0, 0, 'Yrel! The defense crystal is broken. The Iron Horde have overrun Karabor. We\'d need a miracle to break through.', 12, 0, 100, 5, 0, 44813, 79394, 0, 0, 0, 'Vindicator Maraad to Player');
