INSERT INTO `creature_text` VALUES (77321, 3, 0, 'Духи да принесут тебе победу.', 12, 0, 100, 0, 0, 42439, 91903, 73000, 73000, 0, 'Кал\'гор Благородный to Player');
INSERT INTO `creature_text` VALUES (77321, 3, 0, 'Лок-нарош.', 12, 0, 100, 0, 0, 0, 91902, 31000, 31000, 0, 'Кал\'гор Благородный to Player');
INSERT INTO `creature_text` VALUES (77321, 3, 0, 'Хорошо!', 12, 0, 100, 0, 0, 0, 91704, 1546000, 1546000, 0, 'Кал\'гор Благородный to 236982');
