INSERT INTO `creature_text` VALUES (77325, 0, 0, 'Так это вы?! Вы - те самые шавки, которые устроили хаос в моей литейной? Я поставлю вас на место.', 14, 0, 100, 0, 0, 45434, 85848, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 1, 0, 'Открыть заслонки! Залейте все шлаком!', 14, 0, 100, 0, 0, 45448, 85852, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 2, 0, '|TInterface\\Icons\\ability_blackhand_demolition.blp:20|t %s вызывает |cFFFF0000|Hspell:156425|h[обрушение]|h|r!', 41, 0, 100, 0, 0, 45421, 80628, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 3, 0, 'I will bring the foundry to your head!', 14, 0, 100, 0, 0, 45421, 85854, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 4, 0, 'Слишком медленно.', 14, 0, 100, 0, 0, 45450, 85857, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 5, 0, 'Если придется, я здесь все разнесу!', 14, 0, 100, 0, 0, 45422, 85855, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 6, 0, 'Нужно было пригнуться.', 14, 0, 100, 0, 0, 45452, 85859, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 7, 0, 'Всем ЛЕЧЬ!', 14, 0, 100, 1, 0, 45440, 85864, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 8, 0, 'Снайперы! Пристрелите их!', 14, 0, 100, 0, 0, 45431, 85865, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 9, 0, 'Осадные машины! Разрушьте все!', 14, 0, 100, 0, 0, 45458, 85869, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 10, 0, '%s призывает |cFFFF0000осадный танк|r!', 41, 0, 100, 0, 0, 45458, 80490, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 11, 0, 'Никакой дисциплины.', 14, 0, 100, 0, 0, 45436, 85880, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 12, 0, 'Вы слабы.', 14, 0, 100, 0, 0, 45451, 89024, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 13, 0, 'Солдаты! Открыть огонь!', 14, 0, 100, 0, 0, 45432, 85866, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 14, 0, 'Где осадный танк?', 14, 0, 100, 0, 0, 45457, 85868, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 15, 0, 'Вы утонете в огненном чреве литейной!', 14, 0, 100, 0, 0, 45442, 85872, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 16, 0, 'Тебе больше негде прятаться.', 14, 0, 100, 0, 0, 45453, 85860, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 17, 0, 'Машины, огонь! Уничтожьте их любой ценой!', 14, 0, 100, 0, 0, 45459, 85870, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 18, 0, 'Вы все СГОРИТЕ!!', 14, 0, 100, 0, 0, 45441, 85871, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 19, 0, 'И это все? ', 14, 0, 100, 0, 0, 45439, 90608, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 20, 0, 'Трус!', 14, 0, 100, 0, 0, 45454, 85861, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 21, 0, 'Ваше время вышло.', 14, 0, 100, 0, 0, 45429, 85877, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 22, 0, '|TInterface\\Icons\\spell_fire_ragnaros_lavabolt.blp:20|t %s вызывает |cFFFF0000|Hspell:156928|h[Извержение шлака]|h|r!', 41, 0, 100, 0, 0, 45426, 80629, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 23, 0, 'Горнило поглотит вас!', 14, 0, 100, 0, 0, 45426, 85874, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 24, 0, 'Это горнило станет вашей могилой!', 14, 0, 100, 0, 0, 45430, 85878, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 25, 0, 'Бах! И все.', 14, 0, 100, 0, 0, 45438, 85882, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 26, 0, 'В этом котле - ваша смерть.', 14, 0, 100, 0, 0, 45428, 85876, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 27, 0, 'Пламя пожрет ваши трупы.', 14, 0, 100, 0, 0, 45425, 85873, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 28, 0, 'Мы... Мы – орудие вождя... Без нас он...', 14, 0, 100, 0, 0, 45424, 85889, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 29, 0, 'Литейную можно отстроить заново, а вот вас никто по кусочкам не соберет.', 14, 0, 100, 1, 0, 45423, 85856, 0, 0, 0, 'Чернорук to 0');
INSERT INTO `creature_text` VALUES (77325, 30, 0, 'Воины Черной горы, огонь!', 14, 0, 100, 0, 0, 45433, 85867, 0, 0, 0, 'Чернорук to Чернорук');
INSERT INTO `creature_text` VALUES (77325, 31, 0, 'Ничтожество.', 14, 0, 100, 0, 0, 45435, 89715, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77325, 32, 0, '|TInterface\\Icons\\ability_blackhand_marked4death:20|tНа вас поставили |cFFFF0000|Hspell:156096|h[метку смерти]|h|r.', 42, 0, 100, 0, 0, 0, 80788, 0, 0, 0, 'Чернорук to Player');
INSERT INTO `creature_text` VALUES (77332, 3, 0, 'Here Mr. Lizzy... over here.', 12, 0, 100, 0, 0, 0, 85515, 19538, 19541, 0, 'Karabor Child to Player');
