INSERT INTO `creature_text` VALUES (77370, 0, 0, 'Уводи горожан в безопасное место.', 12, 0, 100, 0, 0, 0, 87805, 0, 0, 0, 'Пехотинец Зашедшей Луны to Пехотинец Зашедшей Луны');
INSERT INTO `creature_text` VALUES (77370, 2, 0, 'A fallen guard has been turned into a skeleton!', 41, 0, 100, 0, 0, 0, 91949, 0, 0, 178499, 'Lunarfall Footman to Lunarfall Footman');
INSERT INTO `creature_text` VALUES (77370, 3, 0, 'Spread out! Spread out!', 12, 0, 100, 0, 0, 0, 83925, 355093, 355093, 0, 'Lunarfall Footman to Lunarfall Footman');
INSERT INTO `creature_text` VALUES (77370, 3, 0, 'Еще пара ударов, и все.', 12, 0, 100, 0, 0, 0, 85582, 817000, 817000, 0, 'Пехотинец Зашедшей Луны to Пехотинец Зашедшей Луны');
INSERT INTO `creature_text` VALUES (77370, 3, 0, 'Павший страж превращен в скелет!', 41, 0, 100, 0, 0, 0, 91949, 700000, 700000, 178499, 'Пехотинец Зашедшей Луны to Пехотинец Зашедшей Луны');
INSERT INTO `creature_text` VALUES (77370, 4, 0, 'I lost count. How many was that?', 12, 0, 100, 0, 0, 0, 85716, 34125, 34125, 0, 'Lunarfall Footman to Lunarfall Footman');
INSERT INTO `creature_text` VALUES (77370, 4, 0, 'Just a few whacks more.', 12, 0, 100, 0, 0, 0, 85582, 3031, 3031, 0, 'Lunarfall Footman to Lunarfall Footman');
INSERT INTO `creature_text` VALUES (77370, 4, 0, 'Бей в сердце!', 12, 0, 100, 0, 0, 0, 81065, 40000, 40000, 158600, 'Пехотинец Зашедшей Луны to Лезвие Бездны из клана Призрачной Луны');
INSERT INTO `creature_text` VALUES (77370, 4, 0, 'Устанешь тут считать. Сколько уже?', 12, 0, 100, 0, 0, 0, 85716, 20000, 20000, 1604, 'Пехотинец Зашедшей Луны to Пехотинец Зашедшей Луны');
INSERT INTO `creature_text` VALUES (77376, 3, 0, 'Так, за работу.', 12, 0, 100, 0, 0, 0, 84030, 178290, 178290, 0, 'Дровосек Зашедшей Луны to Помощница Яркий Камень');
INSERT INTO `creature_text` VALUES (77404, 0, 0, 'Сюда, паршивцы!', 14, 0, 100, 0, 0, 41631, 83205, 0, 0, 0, 'Мясник to Player');
INSERT INTO `creature_text` VALUES (77404, 1, 0, 'Мяса нет, жилы одни.', 14, 0, 100, 0, 0, 41632, 83206, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77404, 2, 0, 'Дай его нам живого, трепыхающегося!', 14, 0, 100, 0, 0, 41629, 83201, 0, 0, 0, 'Мясник to Player');
INSERT INTO `creature_text` VALUES (77404, 3, 0, 'Сочно!', 14, 0, 100, 0, 0, 41630, 83202, 0, 0, 0, 'Мясник to Player');
INSERT INTO `creature_text` VALUES (77404, 5, 0, 'Мясо снова в меню!', 14, 0, 100, 0, 0, 41639, 83213, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77404, 6, 0, 'Подходи по одному!', 14, 0, 100, 0, 0, 41626, 83198, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77404, 7, 0, 'I’ll just cut the crown off!', 14, 0, 100, 0, 0, 41636, 83210, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77404, 8, 0, 'Выпустить всю кровь!', 14, 0, 100, 0, 0, 41637, 83211, 0, 0, 0, 'Мясник to Мясник');
INSERT INTO `creature_text` VALUES (77404, 11, 0, 'Пускаю в дело мясорубку!', 14, 0, 100, 0, 0, 41628, 83200, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77404, 37, 0, '%s впадает в ярость!', 41, 0, 100, 0, 0, 41628, 82959, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77404, 58, 0, '%s впадает в ярость берсерка!', 41, 0, 100, 0, 0, 41627, 4428, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77404, 59, 0, 'Я ВАС ЗАЛОМАЮ!', 14, 0, 100, 0, 0, 41627, 83199, 0, 0, 0, 'Мясник to 0');
INSERT INTO `creature_text` VALUES (77411, 0, 0, 'I am FREE!', 12, 0, 100, 396, 0, 45765, 106886, 0, 0, 0, 'Agg\'thok the Eternal to Player');
INSERT INTO `creature_text` VALUES (77411, 1, 0, 'I am in your debt, little one. You have saved my people from an eternity of suffering.', 12, 0, 100, 396, 0, 45766, 80173, 0, 0, 0, 'Agg\'thok the Eternal to Player');
INSERT INTO `creature_text` VALUES (77411, 2, 0, 'We will help you defeat your enemies. We will repay our debt to you, tiny one.', 12, 0, 100, 153, 0, 45767, 80172, 0, 0, 0, 'Agg\'thok the Eternal to Player');
INSERT INTO `creature_text` VALUES (77411, 3, 0, 'I\'m indebted to you, baby. My people are free from eternal suffering.', 12, 0, 100, 396, 0, 45766, 80173, 0, 0, 0, 'Агг\'ток Вечный to Player');
INSERT INTO `creature_text` VALUES (77417, 3, 0, 'Be careful, my friend. Gul\'dan is not to be underestimated no matter how prepared you might be.', 12, 0, 100, 1, 0, 44999, 76041, 670907, 670907, 0, 'Image of Archmage Khadgar to Player');
INSERT INTO `creature_text` VALUES (77417, 3, 0, 'Quickly. Combine the shards to forge a soul trap for Gul\'dan.', 12, 0, 100, 5, 0, 44998, 76039, 650157, 650157, 0, 'Image of Archmage Khadgar to Player');
INSERT INTO `creature_text` VALUES (77417, 3, 0, 'The archmage gestures toward the forge and awaits your return with the fiery heart of Krosnis.', 16, 0, 100, 0, 0, 0, 89335, 514578, 514578, 0, 'Image of Archmage Khadgar to Player');
INSERT INTO `creature_text` VALUES (77417, 3, 0, 'Будь осторожнее. Гул\'дана нельзя недооценивать. Он силен и коварен.', 12, 0, 100, 1, 0, 44999, 76041, 331000, 331000, 0, 'Проекция верховного мага Кадгара to Player');
INSERT INTO `creature_text` VALUES (77417, 3, 0, 'Быстрее. Соедини осколки и сделай ловушку для души Гул\'дана.', 12, 0, 100, 5, 0, 44998, 76039, 302000, 302000, 0, 'Проекция верховного мага Кадгара to Player');
INSERT INTO `creature_text` VALUES (77417, 3, 0, 'Верховный маг указывает на кузницу и ожидает вашего возвращения с огненным сердцем Кросниса.', 16, 0, 100, 0, 0, 1342, 89335, 224000, 224000, 0, 'Проекция верховного мага Кадгара to Player');
INSERT INTO `creature_text` VALUES (77426, 3, 0, 'Братья и сестры! Объединив наши силы, мы сметем дренеев!', 12, 0, 100, 5, 0, 0, 80570, 4555, 4555, 0, 'Темный служитель культа to Player');
INSERT INTO `creature_text` VALUES (77428, 0, 0, 'Верховный Молот не падет. Этим рабам не сокрушить Горианскую империю!', 14, 0, 100, 0, 0, 44768, 84881, 0, 0, 0, 'Император Мар\'гок to Капитан стражи Таг');
INSERT INTO `creature_text` VALUES (77428, 1, 0, 'Довольно, Мал\'грис. Очисти от них тронный зал.', 14, 0, 100, 0, 0, 44769, 84882, 0, 0, 0, 'Император Мар\'гок to Капитан стражи Таг');
INSERT INTO `creature_text` VALUES (77428, 2, 0, 'Кто смеет посягать на трон Верховного Молота?! Это что, происки Чо\'Галла-предателя? Преклоните колени и присягните мне, и, возможно, я сохраню вам жизнь.', 14, 0, 100, 0, 0, 42029, 80750, 0, 0, 0, 'Император Мар\'гок to Player');
INSERT INTO `creature_text` VALUES (77428, 3, 0, 'Вы узнаете, почему я – король, а вы – ничтожества.', 14, 0, 100, 0, 0, 42015, 80751, 0, 0, 0, 'Император Мар\'гок to Player');
INSERT INTO `creature_text` VALUES (77428, 4, 0, 'Магия покорна мне, покоритесь и вы!', 14, 0, 100, 0, 0, 42035, 83525, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 5, 0, 'Сейчас вы поймете свои ошибки.', 14, 0, 100, 0, 0, 42036, 83526, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 6, 0, 'I command magic!', 14, 0, 100, 0, 0, 42038, 83528, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 7, 0, 'Смотрите! Эти камни несут погибель! Они даруют мне невиданную силу!', 14, 0, 100, 408, 0, 42018, 80660, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 8, 0, 'Моя сила безгранична!', 14, 0, 100, 0, 0, 42033, 100188, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 9, 0, 'Прочь с глаз моих.', 14, 0, 100, 0, 0, 42037, 83527, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 14, 0, 'Вы заслужили мой гнев!', 14, 0, 100, 0, 0, 42034, 83524, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 17, 0, 'Это лишь часть моей мощи! А теперь узрите древнюю магию моей империи!', 14, 0, 100, 408, 0, 42019, 80827, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 18, 0, 'Гвардия, защищайте меня! Я должен пробудить силу камня!', 14, 0, 100, 0, 0, 42020, 80828, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 19, 0, 'Какая мощь! И она уничтожит вас!', 14, 0, 100, 0, 0, 42021, 80829, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 20, 0, 'Он полон энергии. И скоро она высвободится!', 14, 0, 100, 0, 0, 42022, 80830, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 21, 0, 'По моим жилам бежит бесконечная мощь камней! Близится ваша погибель!', 14, 0, 100, 15, 0, 42023, 80658, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 23, 0, 'Ты утомляешь меня.', 14, 0, 100, 0, 0, 42032, 83522, 0, 0, 0, 'Император Мар\'гок to Player');
INSERT INTO `creature_text` VALUES (77428, 38, 0, 'Знай свое место!', 14, 0, 100, 0, 0, 42031, 133177, 0, 0, 0, 'Император Мар\'гок to Player');
INSERT INTO `creature_text` VALUES (77428, 143, 0, 'Хватит! Короли-чародеи веками охраняли эти залы. И сейчас вы узнаете, почему.', 14, 0, 100, 408, 0, 42024, 80832, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 144, 0, 'Стража! Убрать это отребье!', 14, 0, 100, 0, 0, 42025, 80833, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77428, 145, 0, 'Эта сила беспредельна!', 14, 0, 100, 0, 0, 42026, 80834, 0, 0, 0, 'Император Мар\'гок to 0');
INSERT INTO `creature_text` VALUES (77434, 1, 0, 'You\'ll rot with the draenei.', 12, 0, 100, 0, 0, 0, 83655, 0, 0, 0, 'Zealous Fanatic');
INSERT INTO `creature_text` VALUES (77434, 1, 0, 'Не видать дренеям свободы!', 12, 0, 100, 71, 0, 0, 83654, 0, 0, 0, 'Ревностная фанатичка');
