INSERT INTO `creature_text` VALUES (77776, 3, 0, 'Возьми мой меч, что прислонен к камню. И пусть он направит тебя в тот час, когда твоя вера пошатнется.', 12, 0, 100, 0, 0, 0, 80520, 28798, 28798, 0, 'Странствующий воздаятель');
INSERT INTO `creature_text` VALUES (77776, 4, 0, 'I ... I feel the Light again! The curse is no longer domineering over me. And ... I see the way to Auchindoun.', 12, 0, 100, 0, 0, 0, 80510, 1404, 1404, 0, 'Странствующий воздаятель');
INSERT INTO `creature_text` VALUES (77776, 4, 0, 'I... I can feel the Light again! I am freed of her curse. I... I see the way to Auchindoun.', 12, 0, 100, 0, 0, 0, 80510, 57219, 57219, 0, 'Wandering Vindicator');
INSERT INTO `creature_text` VALUES (77776, 4, 0, 'The old paladin suddenly gasps. A look of rapture crosses his face and he blinks, seeing you for the first time.', 16, 0, 100, 0, 0, 0, 80509, 53625, 53625, 157241, 'Wandering Vindicator');
INSERT INTO `creature_text` VALUES (77784, 4, 0, 'No! Stop! I can\'t control him!', 14, 0, 100, 0, 0, 0, 86513, 9937, 9937, 0, 'Lo\'marg Jawcrusher');
INSERT INTO `creature_text` VALUES (77784, 4, 0, 'Нет! Остановите его! Он вышел из-под контроля!', 14, 0, 100, 0, 0, 0, 86513, 11000, 11000, 169798, 'Ло\'марг Дробитель Зубов');
INSERT INTO `creature_text` VALUES (77786, 3, 0, 'Take one step closer and I\'ll gut you like a fish, orc!', 12, 0, 100, 0, 0, 0, 80522, 2733296, 2733296, 0, 'Jenaa to Player');
