INSERT INTO `creature_text` VALUES (77857, 3, 0, 'Они порабощают моих детей, заставляют их драться и умирать. Но что мы можем сделать?', 12, 0, 100, 0, 0, 46791, 87185, 0, 0, 0, 'Ка\'алу to Player');
INSERT INTO `creature_text` VALUES (77857, 4, 0, 'Анзу вечно впутывался в дела смертных. Мне стоило бы учиться на его ошибках.', 12, 0, 100, 0, 0, 46792, 87188, 0, 0, 0, 'Ка\'алу to Player');
INSERT INTO `creature_text` VALUES (77857, 5, 0, 'Ладно, хранитель свитков. Бери мою стаю.', 12, 0, 100, 0, 0, 46793, 87189, 0, 0, 0, 'Ка\'алу to Player');
