INSERT INTO `creature_text` VALUES (78346, 3, 0, 'How naive you are. This place is nothing but a shell, a husk, to be torn and discarded from the fruit of power that I shall devour!', 14, 0, 100, 0, 0, 0, 81014, 296625, 296625, 0, 'Kaelynara Sunchaser to Player');
