INSERT INTO `creature_text` VALUES (78346, 3, 0, 'Какая сила, какие возможности... Скрытая энергия этого кристалла больше не должна пропадать впустую!', 14, 0, 100, 0, 0, 0, 81013, 100000, 100000, 0, 'Келинара Ловчая Солнца to Player');
