INSERT INTO `creature_text` VALUES (78741, 3, 0, '$p, я не смогу долго поддерживать эту преграду. Убей Келинару, пока не стало слишком поздно!', 12, 0, 100, 0, 0, 0, 81012, 4000, 4000, 0, 'Верховный маг Эландра to Player');
