INSERT INTO `creature_text` VALUES (78741, 3, 0, 'When you are ready, I will teleport you back to Jorune Mine, $p.', 12, 0, 100, 0, 0, 0, 81191, 56953, 56953, 0, 'Archmage Elandra to Player');
