INSERT INTO `creature_text` VALUES (78928, 3, 0, 'And twist them into the power we need.  Into the power we deserve!', 12, 0, 100, 0, 0, 0, 81273, 21032, 21032, 0, 'Mouthpiece of Ner\'zhul');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Back from the power needed to save our clan!', 12, 0, 100, 0, 0, 0, 81269, 221407, 221407, 0, 'Mouthpiece of Ner\'zhul');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Our ancestors once prohibited the use of the dark magic, they held us back.', 12, 0, 100, 0, 0, 0, 81268, 64782, 215360, 0, 'Mouthpiece of Ner\'zhul');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'So I say to you my brothers and sisters of the Shadowmoon clan; use the skills passed down from age to age!', 12, 0, 100, 0, 0, 0, 81270, 227500, 227500, 0, 'Mouthpiece of Ner\'zhul');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Use them and summon forth the spirits of our ancestors.', 12, 0, 100, 0, 0, 0, 81271, 233500, 233500, 0, 'Mouthpiece of Ner\'zhul');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'We must join the Iron Horde else we will be crushed by it.  We must prove our worth!', 12, 0, 100, 0, 0, 0, 81267, 209219, 209219, 0, 'Mouthpiece of Ner\'zhul');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Воспользуйтесь ими и призовите духов наших предков.', 12, 0, 100, 0, 0, 0, 81271, 486000, 486000, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'И превратите их в силу, которая нам нужна. В силу, которую мы заслужили!', 12, 0, 100, 0, 0, 0, 81273, 128516, 128516, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'И я говорю вам, братья и сестры Призрачной Луны: обратитесь к талантам, которые передаются из поколения в поколение!', 12, 0, 100, 0, 0, 0, 81270, 314000, 480000, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Когда-то наши предки запретили нам использовать темную магию и не давали нам развиваться.', 12, 0, 100, 0, 0, 0, 81268, 19000, 19000, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Мы должны вступить в Железную Орду, иначе она сокрушит нас. Нам надо доказать, что мы достойны ее!', 12, 0, 100, 0, 0, 0, 81267, 13000, 13000, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Подчините их своей воле.', 12, 0, 100, 0, 0, 0, 81272, 122344, 122344, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78928, 3, 0, 'Только тогда будущее клана Призрачной Луны будет прочным!', 12, 0, 100, 0, 0, 0, 81275, 134719, 134719, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78928, 4, 0, 'Они лишили нас силы, чтобы спасти клан!', 12, 0, 100, 0, 0, 0, 81269, 6000, 6000, 0, 'Выразитель воли Нер\'зула');
INSERT INTO `creature_text` VALUES (78932, 0, 0, '$n пытается освободить союзника от всех эффектов контроля!', 16, 0, 100, 0, 0, 0, 81472, 0, 0, 0, 'Неистовый призыватель бури to Неистовый призыватель бури');
