INSERT INTO `creature_text` VALUES (79187, 1, 0, 'Еще одна душа для Терон\'крова!', 12, 0, 100, 273, 0, 0, 91183, 0, 0, 0, 'Темная похитительница разума');
INSERT INTO `creature_text` VALUES (79187, 2, 0, 'Моя смерть... ничего не изменит...', 12, 0, 100, 25, 0, 0, 125084, 0, 0, 0, 'Темная похитительница разума to Анго\'рошский крушитель');
