INSERT INTO `creature_text` VALUES (79187, 3, 0, 'Another soul for Teron\'gor!', 12, 0, 100, 6, 0, 0, 91183, 3500, 3500, 0, 'Shadowborne Dementor');
INSERT INTO `creature_text` VALUES (79187, 3, 0, 'Ах, свежая душа!', 12, 0, 100, 5, 0, 0, 91182, 715000, 715000, 0, 'Темная похитительница разума');
INSERT INTO `creature_text` VALUES (79187, 3, 0, 'Да, Гул\'дан. Твоя воля будет исполнена.', 12, 0, 100, 1, 0, 0, 81368, 9734, 9734, 0, 'Темная похитительница разума to Player');
INSERT INTO `creature_text` VALUES (79187, 3, 0, 'Скоро Аукиндон будет нашим. Жаль, что ты этого уже не увидишь.', 12, 0, 100, 153, 0, 0, 80393, 293000, 293000, 0, 'Темная похитительница разума to Player');
