INSERT INTO `creature_text` VALUES (79187, 3, 0, 'Терон\'кров будет торжествовать!', 12, 0, 100, 0, 0, 0, 91185, 120000, 120000, 0, 'Темная похитительница разума');
INSERT INTO `creature_text` VALUES (79187, 4, 0, 'Терон\'кров... прими мою душу...', 12, 0, 100, 0, 0, 0, 91186, 11000, 11000, 0, 'Темная похитительница разума to Player');
INSERT INTO `creature_text` VALUES (79187, 4, 0, 'Ты скоро ко мне присоединишься.', 12, 0, 100, 0, 0, 0, 91188, 27000, 27000, 0, 'Темная похитительница разума to Player');
INSERT INTO `creature_text` VALUES (79188, 0, 0, 'Обычный гладиатор жестко бьет с правой или с левой, а вот Молотобой... Он вообще жесткий!', 12, 0, 100, 0, 0, 0, 82457, 0, 0, 0, 'Дексил Клинок Ужаса to Player');
INSERT INTO `creature_text` VALUES (79188, 1, 0, 'Вспарывающая Плоть ест только в том случае, если побеждает в поединке... И, скажем так: она не голодает!', 12, 0, 100, 0, 0, 0, 82458, 0, 0, 0, 'Дексил Клинок Ужаса to Player');
INSERT INTO `creature_text` VALUES (79188, 2, 0, 'Ты $gубил:убила; Вспарывающую Плоть и $gсокрушил:сокрушила; Молотобоя! Вперед, сделай то же самое с Хе\'йилаа! Удачи!', 12, 0, 100, 0, 0, 0, 82459, 0, 0, 0, 'Дексил Клинок Ужаса to Player');
INSERT INTO `creature_text` VALUES (79188, 3, 0, 'Одолеть Бумшмяка еще никто не смог! Он разбирается с противником так же быстро, как огонь – с соломенной крышей!', 12, 0, 100, 0, 0, 0, 82460, 0, 0, 0, 'Дексил Клинок Ужаса to Player');
INSERT INTO `creature_text` VALUES (79188, 4, 0, '$n, это все! Роакк – последний! Давай, задай жару!', 12, 0, 100, 0, 0, 0, 82461, 0, 0, 0, 'Дексил Клинок Ужаса to Player');
INSERT INTO `creature_text` VALUES (79188, 5, 0, 'Эй... а ты чего $gожидал:ожидала;? Ледяную Скорбь или что-то вроде того?', 12, 0, 100, 1, 0, 0, 91406, 0, 0, 0, 'Дексил Клинок Ужаса to Player');
INSERT INTO `creature_text` VALUES (79191, 3, 0, 'Проницатель Бездны вас заметил! Уходите, пока он не напал!', 42, 0, 100, 0, 0, 0, 81454, 28000, 28000, 0, 'Проницатель Бездны to Player');
INSERT INTO `creature_text` VALUES (79194, 0, 0, 'На этом клинке чьи-то инициалы... на арене, что ли, подобран?', 12, 0, 100, 1, 0, 0, 84705, 0, 0, 0, 'Зритель арены to 0');
INSERT INTO `creature_text` VALUES (79194, 1, 0, 'Этот клинок весь выщербленный и ржавый – а ты уверял меня, что его только что выковали!', 12, 0, 100, 273, 0, 0, 84703, 0, 0, 0, 'Зритель арены to 0');
INSERT INTO `creature_text` VALUES (79199, 0, 0, 'Свежие бургеры! Налетай! Ты сосиски любишь? Из копытней такие сосисочки получаются – пальчики оближешь!', 12, 0, 100, 1, 0, 0, 84786, 0, 0, 0, 'Шеф-повар Снофлюкс to Player');
INSERT INTO `creature_text` VALUES (79201, 3, 0, 'Мы не можем допустить, чтобы эта реликвия попала в лапы ограм! Ей место в частной коллекции!', 12, 0, 100, 0, 0, 0, 84640, 928000, 928000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (79201, 3, 0, 'Слышишь этот звук, $p? Это удача стучится в твою дверь, и у нее с собой куча денег!', 12, 0, 100, 0, 0, 0, 82463, 13844, 13844, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (79201, 3, 0, 'Увидимся на следующих раскопках, $n! Без тебя мы бы не справились!', 12, 0, 100, 0, 0, 0, 84648, 1692000, 1692000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (79204, 0, 0, 'Собираешься кого-нибудь разрубить, проткнуть, расплющить или превратить в решето? У меня найдется оружие на любой вкус!', 12, 0, 100, 1, 0, 0, 84790, 0, 0, 0, 'Трокси Трескозвонц to Player');
INSERT INTO `creature_text` VALUES (79210, 0, 0, 'Эй, выпусти меня уже!', 12, 0, 100, 0, 0, 45830, 82001, 0, 0, 0, 'Моркет Воющий Меч to Player');
INSERT INTO `creature_text` VALUES (79220, 1, 0, '%s will be armed in 3 seconds!', 16, 0, 100, 0, 0, 0, 10894, 0, 0, 11817, 'Ironmarch Land Mine to Ironmarch Warsmith');
INSERT INTO `creature_text` VALUES (79220, 4, 0, '%s is now armed!', 16, 0, 100, 0, 0, 0, 3543, 4969, 4969, 160078, 'Ironmarch Land Mine to Ironmarch Warsmith');
INSERT INTO `creature_text` VALUES (79223, 0, 0, '$gНичтожный:Ничтожная:r; |3-6($r)! Мне на тебя даже смотреть противно!', 12, 0, 100, 0, 0, 0, 86634, 0, 0, 0, 'Чародей из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (79223, 2, 0, 'Ты слишком $gслаб:слаба; для гладиатора! Это что, шутка какая-то? Проваливай!', 12, 0, 100, 0, 0, 0, 86632, 0, 0, 0, 'Чародей из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (79223, 3, 0, 'Мы пускаем только гладиаторов! Если ты не драться $gпришел:пришла; – значит, тебе здесь делать нечего!', 12, 0, 100, 0, 0, 0, 86631, 0, 0, 0, 'Чародей из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (79230, 1, 0, 'О! Вот на вас я и испытаю новые ловушки.', 12, 0, 100, 0, 0, 0, 89541, 0, 0, 0, 'Охотник на гроннов Рокаш');
INSERT INTO `creature_text` VALUES (79230, 3, 0, 'Все еще на вершине пищевой цепочки, командир?', 12, 0, 100, 0, 0, 0, 89545, 88530, 88530, 0, 'Охотник на гроннов Рокаш to Player');
INSERT INTO `creature_text` VALUES (79230, 3, 0, 'Подумать только!', 12, 0, 100, 547, 0, 0, 79412, 193004, 193004, 0, 'Охотник на гроннов Рокаш to 232416');
INSERT INTO `creature_text` VALUES (79230, 4, 0, 'Ты $gвыбрал:выбрала; не ту сторону!', 12, 0, 100, 0, 0, 0, 89544, 3000, 20000, 171943, 'Охотник на гроннов Рокаш to Фамилиар пламени Тьмы');
INSERT INTO `creature_text` VALUES (79231, 1, 0, 'Здесь только огры! Ты не огр!', 12, 0, 100, 25, 0, 0, 91172, 0, 0, 147086, 'Крушитель из клана Гордунни');
INSERT INTO `creature_text` VALUES (79231, 1, 0, 'Из какой помойной кучи ты вылез?', 12, 0, 100, 0, 0, 0, 91178, 0, 0, 0, 'Крушитель из клана Гордунни');
INSERT INTO `creature_text` VALUES (79231, 1, 0, 'Твоя голова пополнит мою коллекцию!', 12, 0, 100, 0, 0, 0, 91175, 0, 0, 0, 'Крушитель из клана Гордунни');
