INSERT INTO `creature_text` VALUES (79231, 3, 0, 'Поклонись Моркурку!', 12, 0, 100, 1, 0, 0, 91173, 30000, 30000, 0, 'Крушитель из клана Гордунни');
INSERT INTO `creature_text` VALUES (79231, 3, 0, 'Рядом с крепостью чужаки! К оружию!', 14, 0, 100, 0, 0, 0, 82689, 15000, 15000, 0, 'Крушитель из клана Гордунни to Player');
INSERT INTO `creature_text` VALUES (79231, 3, 0, 'Ты $gсильный:сильная;... чересчур $gсильный:сильная; для своего росточка…', 12, 0, 100, 25, 0, 0, 91179, 9000, 9000, 164247, 'Крушитель из клана Гордунни to Player');
INSERT INTO `creature_text` VALUES (79231, 4, 0, 'You strong... for something so small...', 12, 0, 100, 0, 0, 0, 91179, 22344, 22344, 0, 'Gordunni Basher to Magister Serena');
