INSERT INTO `creature_text` VALUES (79233, 3, 0, 'Да хранят тебя духи, командир.', 12, 0, 100, 0, 0, 0, 90703, 103000, 103000, 0, 'Локра to Player');
INSERT INTO `creature_text` VALUES (79234, 1, 0, 'What hole you crawl out of?', 12, 0, 100, 0, 0, 0, 91178, 0, 0, 0, 'Gordunni Cleaver');
