INSERT INTO `creature_text` VALUES (79234, 2, 0, 'Ты $gсильный:сильная;... чересчур $gсильный:сильная; для своего росточка…', 12, 0, 100, 0, 0, 0, 91179, 0, 0, 164257, 'Рассекатель из клана Гордунни to Магистр Серена');
INSERT INTO `creature_text` VALUES (79234, 3, 0, 'For Morkurk! For the empire!', 14, 0, 100, 0, 0, 0, 82690, 4422, 4422, 0, 'Gordunni Cleaver to Player');
INSERT INTO `creature_text` VALUES (79234, 3, 0, 'Intruders approach the keep! To arms!', 14, 0, 100, 0, 0, 0, 82689, 71937, 71937, 0, 'Gordunni Cleaver to Player');
INSERT INTO `creature_text` VALUES (79234, 3, 0, 'За Моркурка! За империю!', 14, 0, 100, 0, 0, 44021, 82690, 4719, 4719, 0, 'Рассекатель из клана Гордунни to Player');
INSERT INTO `creature_text` VALUES (79234, 3, 0, 'Моркурк скоро тебя победит!', 12, 0, 100, 0, 0, 0, 91181, 10000, 10000, 0, 'Рассекатель из клана Гордунни to Player');
INSERT INTO `creature_text` VALUES (79234, 3, 0, 'Поклонись Моркурку!', 12, 0, 100, 0, 0, 0, 91173, 66391, 66391, 0, 'Рассекатель из клана Гордунни');
INSERT INTO `creature_text` VALUES (79234, 3, 0, 'Рядом с крепостью чужаки! К оружию!', 14, 0, 100, 0, 0, 0, 82689, 30000, 30000, 0, 'Рассекатель из клана Гордунни to Player');
INSERT INTO `creature_text` VALUES (79234, 4, 0, 'Это ты как так $gсделал:сделала;?', 12, 0, 100, 0, 0, 0, 91180, 6000, 6000, 164257, 'Рассекатель из клана Гордунни to Player');
INSERT INTO `creature_text` VALUES (79241, 3, 0, 'Надеюсь, я не ошибся, когда поверил в вас.', 12, 0, 100, 0, 0, 45382, 82400, 18428, 18428, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (79243, 0, 0, 'If you mark those trees, Shelly\'s lumberjacks will do the rest.', 12, 0, 100, 1, 0, 43525, 81726, 0, 0, 0, 'Baros Alexston to Player');
INSERT INTO `creature_text` VALUES (79243, 1, 0, 'Giant killer ravens... I have a feeling it\'s going to be a long, cold night.', 12, 0, 100, 1, 0, 43527, 81727, 0, 0, 0, 'Baros Alexston to Player');
INSERT INTO `creature_text` VALUES (79243, 2, 0, 'An excellent choice of lumber, if I do say so myself.', 12, 0, 100, 1, 0, 43526, 81885, 0, 0, 0, 'Baros Alexston to Player');
INSERT INTO `creature_text` VALUES (79243, 3, 0, 'Impressive work, commander. I know I\'ll sleep a lot better without those giant killer birds flying around.', 12, 0, 100, 1, 0, 43528, 81886, 0, 0, 0, 'Baros Alexston to Player');
INSERT INTO `creature_text` VALUES (79243, 4, 0, 'Let me know when you are ready to start construction, commander.', 12, 0, 100, 1, 0, 0, 81887, 0, 0, 0, 'Baros Alexston to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, '... Protector ... still obeys her ... destroy him!', 14, 0, 100, 0, 0, 44645, 81537, 298000, 298000, 0, 'Стражница душ Туулани to Стражница душ Ниами');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Ahhh!', 12, 0, 100, 0, 0, 46435, 122062, 174047, 174047, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Beautiful, isn\'t it? Just think - you\'re one of the first outsiders to see inside Auchindoun.', 12, 0, 100, 6, 0, 44655, 81530, 28078, 28078, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Good! We must have arrived before Teron\'gor and Gul\'dan!', 12, 0, 100, 3, 0, 44644, 81528, 8094, 8094, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Here - this is not enough to thank you for your actions, but it is what we have to give.', 12, 0, 100, 1, 0, 44651, 81543, 4640, 4640, 0, 'Soulbinder Tuulani to 77734');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'I will gather those who remain faithful to us and try to protect them. Please stop it!', 12, 0, 100, 25, 0, 44649, 81541, 422000, 422000, 0, 'Стражница душ Туулани to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'I\'ll gather any who remain loyal here and protect them. You - please, go put a stop to this madness!', 12, 0, 100, 25, 0, 44649, 81541, 192375, 192375, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Let us proceed.', 12, 0, 100, 25, 0, 44657, 81532, 55360, 55360, 0, 'Soulbinder Tuulani to Invisible Stalker');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Now we can rebuild... we must rebuild. We will never allow our souls to fall to the Legion.', 12, 0, 100, 1, 0, 44652, 81544, 15125, 15125, 0, 'Soulbinder Tuulani to 77734');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Nyami is working to bolster the defenses against their assault - follow me, we will report to her.', 12, 0, 100, 1, 0, 44654, 81529, 12281, 12281, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'She had help. So many of our order were corrupt...', 12, 0, 100, 603, 0, 44648, 81540, 186359, 186359, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'She... got away. The defenses...', 12, 0, 100, 1, 0, 44647, 81539, 179063, 179063, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Strange... why is the door sealed? No matter...', 12, 0, 100, 6, 0, 44656, 81531, 46094, 46094, 0, 'Soulbinder Tuulani to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'The Vindicators and Exarchs of the past. Heroes all - their souls still remain with us here.', 12, 0, 100, 25, 0, 44658, 81533, 69125, 69125, 0, 'Soulbinder Tuulani to Invisible Stalker');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'You have done more than you know. Our people can never thank you enough.', 12, 0, 100, 1, 0, 44653, 81545, 24031, 24031, 0, 'Soulbinder Tuulani to 77734');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'You... you did it!', 14, 0, 100, 5, 0, 44650, 81542, 2625, 2625, 0, 'Soulbinder Tuulani to 77734');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Аххх!', 12, 0, 100, 0, 0, 46435, 81538, 405000, 405000, 0, 'Стражница душ Туулани to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Вот, возьмите. Я знаю, этого недостаточно, чтобы отблагодарить вас, но все-таки…', 12, 0, 100, 1, 0, 44651, 81543, 5000, 5000, 0, 'Стражница душ Туулани to 77734');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Вы даже не представляете, как много сделали для нас. Спасибо вам.', 12, 0, 100, 1, 0, 44653, 81545, 23000, 23000, 0, 'Стражница душ Туулани to 77734');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Вы… вы справились!', 14, 0, 100, 1, 0, 44650, 81542, 3000, 3000, 0, 'Стражница душ Туулани to 77734');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Ей помогли. Сколько же среди нас предателей…', 12, 0, 100, 603, 0, 44648, 81540, 416000, 416000, 0, 'Стражница душ Туулани to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Идем дальше.', 12, 0, 100, 25, 0, 44657, 81532, 66000, 66000, 0, 'Стражница душ Туулани to Невидимый охотник');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Красиво, да? До вас мало кто из чужестранцев видел Аукиндон изнутри.', 12, 0, 100, 6, 0, 44655, 81530, 37000, 37000, 0, 'Стражница душ Туулани to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Ниами укрепляет оборону в ожидании их нападения. Пойдемте к ней.', 12, 0, 100, 1, 0, 44654, 81529, 21000, 21000, 0, 'Стражница душ Туулани to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Она… сбежала… Защита…', 12, 0, 100, 1, 0, 44647, 81539, 409000, 409000, 0, 'Стражница душ Туулани to Player');
INSERT INTO `creature_text` VALUES (79248, 3, 0, 'Отлично! Мы прибыли раньше Терон\'крова и Гул\'дана!', 12, 0, 100, 3, 0, 44644, 81528, 17000, 17000, 0, 'Стражница душ Туулани to Player');
