INSERT INTO `creature_text` VALUES (79521, 3, 0, 'We couldn\'t stop the Dark Star, Prophet. The Iron Horde have taken the harbor!', 12, 0, 100, 5, 0, 45674, 81632, 87719, 87719, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (79521, 3, 0, 'Держитесь! Обороняйте защитный кристалл любой ценой!', 14, 0, 100, 5, 0, 45673, 81623, 24000, 24000, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (79521, 3, 0, 'Мы не остановили Темную Звезду. Железная Орда захватила гавань!', 12, 0, 100, 5, 0, 45674, 81632, 29000, 29000, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (79521, 3, 0, 'Нет! Кристалл был единственной надеждой.', 12, 0, 100, 0, 0, 45677, 81626, 131000, 131000, 0, 'Ирель to Командир Ворка');
INSERT INTO `creature_text` VALUES (79521, 3, 0, 'Они прорвались. Отходим к лестнице! Это наш решающий бой.', 12, 0, 100, 5, 0, 45678, 81689, 137000, 137000, 0, 'Ирель to Командир Ворка');
INSERT INTO `creature_text` VALUES (79521, 3, 0, 'Осторожно! Железные капсулы!', 14, 0, 100, 5, 0, 45675, 81633, 35000, 35000, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (79521, 4, 0, 'Смотрите, командир флота!', 12, 0, 100, 397, 0, 45676, 82587, 63000, 63000, 0, 'Ирель to 450');
INSERT INTO `creature_text` VALUES (79522, 3, 0, 'As long as I live, Karabor shall not fall!', 12, 0, 100, 5, 0, 45358, 81700, 290000, 290000, 0, 'Prophet Velen to Player');
INSERT INTO `creature_text` VALUES (79522, 3, 0, 'I cannot... hold them... back...', 12, 0, 100, 0, 0, 45359, 81823, 310656, 310656, 0, 'Prophet Velen to Player');
INSERT INTO `creature_text` VALUES (79522, 3, 0, 'The Temple of Karabor? No... it cannot be. We are under attack!', 12, 0, 100, 1, 0, 45356, 81621, 76891, 76891, 0, 'Prophet Velen to Player');
INSERT INTO `creature_text` VALUES (79522, 3, 0, 'Не могу… их… удержать…', 12, 0, 100, 0, 0, 45359, 81823, 184000, 184000, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (79522, 3, 0, 'Пока я жив, Карабор не падет!', 12, 0, 100, 5, 0, 45358, 81700, 164000, 164000, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (79522, 3, 0, 'Храм Карабора? Нет… не может быть! На нас напали!', 12, 0, 100, 1, 0, 45356, 81622, 19000, 19000, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (79522, 4, 0, 'Stay close to me, champion!', 12, 0, 100, 1, 0, 45357, 81627, 16328, 16328, 0, 'Prophet Velen to Commander Vorka');
INSERT INTO `creature_text` VALUES (79522, 4, 0, 'Держись ближе ко мне, герой!', 12, 0, 100, 1, 0, 45357, 81627, 4703, 4703, 0, 'Пророк Велен to Командир Ворка');
INSERT INTO `creature_text` VALUES (79524, 4, 0, 'Turn away to avoid Hypnocroak\'s |cFFFF0000|Hspell:160664|h[Hypnotic Gaze]|h|r', 42, 0, 100, 0, 0, 0, 81612, 6891, 20422, 177965, 'Hypnocroak to Player');
INSERT INTO `creature_text` VALUES (79529, 4, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 38630, 8218, 8218, 18501, 'Frostfire Gronnling');
INSERT INTO `creature_text` VALUES (79529, 4, 0, '%s is furious!', 16, 0, 100, 0, 0, 0, 38630, 14696, 14696, 18501, 'Малый гронн Ледяного Огня');
INSERT INTO `creature_text` VALUES (79532, 3, 0, 'I heard Zorkra and her followers barely made it into Talador before orcs got them.', 12, 0, 100, 1, 0, 0, 83319, 6125, 6125, 0, 'Arakkoa Outcast to Player');
INSERT INTO `creature_text` VALUES (79532, 3, 0, 'Говорят, Зоркра со своим отрядом едва успела добраться до Таладора и не попасться в лапы оркам.', 12, 0, 100, 1, 0, 0, 83319, 4000, 1230000, 0, 'Араккоа-изгой to Player');
INSERT INTO `creature_text` VALUES (79532, 3, 0, 'Может быть, нам и правда следует стать на сторону Айкисса...', 12, 0, 100, 1, 0, 0, 83320, 204000, 707000, 0, 'Араккоа-изгой to Player');
INSERT INTO `creature_text` VALUES (79532, 3, 0, 'Не стоило становиться у него на пути.', 12, 0, 100, 1, 0, 0, 83326, 223000, 727000, 0, 'Араккоа-изгой to Player');
INSERT INTO `creature_text` VALUES (79532, 3, 0, 'Ну а что тогда? Присоединиться к Искару в его идиотской священной войне против Приверженцев?', 12, 0, 100, 1, 0, 0, 83325, 215000, 719000, 0, 'Араккоа-изгой to Player');
INSERT INTO `creature_text` VALUES (79532, 3, 0, 'Пока нам надо просто исчезнуть. Спрятаться и выжить.', 12, 0, 100, 1, 0, 0, 83329, 229000, 734000, 0, 'Араккоа-изгой to Player');
INSERT INTO `creature_text` VALUES (79532, 3, 0, 'Чепуха! Я лучше сдохну, чем буду служить этому психу.', 12, 0, 100, 15, 0, 0, 83323, 209000, 712000, 0, 'Араккоа-изгой to Player');
INSERT INTO `creature_text` VALUES (79537, 3, 0, 'Вперед, братья и сестры! Мы отомстим за павших! Отправим этих орков в царство вечного мрака!', 14, 0, 100, 0, 0, 44596, 83382, 161647, 161647, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (79539, 3, 0, 'Ah, an exotic traveler from the north. Come, have a look at my wares!', 12, 0, 100, 3, 0, 0, 83332, 5250, 258812, 0, 'Azik to Player');
INSERT INTO `creature_text` VALUES (79539, 3, 0, 'А, $gтаинственный путешественник:таинственная путешественница; с севера. Взгляни на мои товары!', 12, 0, 100, 3, 0, 0, 83332, 9000, 1224000, 0, 'Азик to Player');
INSERT INTO `creature_text` VALUES (79540, 1, 0, 'Вкусные души...', 12, 0, 100, 0, 0, 0, 91190, 0, 0, 0, 'Наложница-мучительница');
INSERT INTO `creature_text` VALUES (79540, 1, 0, 'Мы есть врата в забвение.', 12, 0, 100, 0, 0, 0, 91192, 0, 0, 0, 'Наложница-мучительница');
INSERT INTO `creature_text` VALUES (79540, 1, 0, 'Темнород голоден!', 12, 0, 100, 0, 0, 0, 91191, 0, 0, 0, 'Наложница-мучительница');
INSERT INTO `creature_text` VALUES (79540, 4, 0, 'Я буду жить... снова...', 12, 0, 100, 0, 0, 0, 91202, 11000, 11000, 0, 'Наложница-мучительница to Player');
INSERT INTO `creature_text` VALUES (79543, 1, 0, 'Я вижу в грядущем... твое поражение.', 12, 0, 100, 0, 0, 0, 83620, 0, 0, 0, 'Ширзир to Player');
INSERT INTO `creature_text` VALUES (79543, 3, 0, 'I\'ve been watching you, $r. Come closer.', 12, 0, 100, 0, 0, 0, 83619, 969, 969, 0, 'Shirzir to Player');
INSERT INTO `creature_text` VALUES (79543, 3, 0, 'Я за тобой наблюдал, |3-6($r). Подойди поближе.', 12, 0, 100, 0, 0, 0, 83619, 3000, 3000, 0, 'Ширзир to Player');
INSERT INTO `creature_text` VALUES (79544, 1, 0, 'Ну что, пора дать этим псам их \"лекарство\".', 12, 0, 100, 0, 0, 0, 88459, 0, 0, 0, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79544, 1, 0, 'Твои мучения доставят мне море удовольствия.', 12, 0, 100, 0, 0, 0, 88460, 0, 0, 0, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79544, 1, 0, 'Твой череп послужит отличным трофеем!', 12, 0, 100, 0, 0, 0, 88461, 0, 0, 0, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79544, 2, 0, 'Каргат будет недоволен.', 12, 0, 100, 0, 0, 0, 88466, 0, 0, 174438, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79544, 3, 0, 'Ты будешь молить о смерти!', 12, 0, 100, 0, 0, 0, 88463, 23000, 23000, 0, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79544, 4, 0, 'Kargath will not be pleased.', 12, 0, 100, 0, 0, 0, 88466, 62640, 62640, 174438, 'Shattered Hand Grunt to 510');
INSERT INTO `creature_text` VALUES (79544, 4, 0, 'My torment ends.', 12, 0, 100, 0, 0, 0, 88465, 86078, 86078, 0, 'Shattered Hand Grunt to Player');
INSERT INTO `creature_text` VALUES (79544, 4, 0, 'The pain... feels so good...', 12, 0, 100, 0, 0, 0, 88467, 15750, 15750, 174439, 'Shattered Hand Grunt to 510');
INSERT INTO `creature_text` VALUES (79544, 4, 0, 'Боль... так сладка...', 12, 0, 100, 0, 0, 0, 88467, 13000, 13000, 0, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79544, 4, 0, 'Моим мучениям пришел конец.', 12, 0, 100, 0, 0, 0, 88465, 13000, 13000, 0, 'Рубака из клана Изувеченной Длани to 58965');
INSERT INTO `creature_text` VALUES (79544, 4, 0, 'Моя смерть ничего не значит.', 12, 0, 100, 0, 0, 0, 88464, 9000, 9000, 174438, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79544, 4, 0, 'Тебе правда так не терпится умереть?', 12, 0, 100, 0, 0, 0, 88462, 12000, 12000, 0, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (79545, 3, 0, 'А ну живо, в строй!', 14, 0, 100, 0, 0, 46118, 83070, 802000, 802000, 0, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 3, 0, 'Гото-о-о-вьсь!!!', 14, 0, 100, 0, 0, 46109, 82203, 1021000, 1021000, 0, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 3, 0, 'Кто смеет бросить мне вызов?', 14, 0, 100, 0, 0, 46116, 82202, 1082000, 1082000, 0, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 3, 0, 'Ну же, свиньи! Давайте, шевелитесь!', 14, 0, 100, 0, 0, 46119, 83065, 972000, 972000, 0, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 3, 0, 'Они здесь... Шевелитесь.', 14, 0, 100, 0, 0, 46117, 83066, 755000, 755000, 0, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 4, 0, '|TInterface\\Icons\\inv_misc_pyriumgrenade:20|t $n подбирает |cFFFF0000|Hspell:160703|h[гранаты клана Черной горы]|h|r!', 41, 0, 100, 0, 0, 46111, 81635, 88000, 243000, 21912, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 4, 0, '|TInterface\\Icons\\Spell_Fire_Fireball02:20|t $n подбирает |cFFFF0000|Hspell:160702|h[мортирные ядра клана Черной горы]|h|r!', 41, 0, 100, 0, 0, 46114, 81634, 61000, 208000, 21912, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 4, 0, '|TInterface\\Icons\\spell_shaman_improvedfirenova:20|t $n подбирает |cFFFF0000|Hspell:156357|h[шрапнель Черной горы]|h|r!', 41, 0, 100, 0, 0, 0, 81636, 68000, 68000, 0, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'БЕГЛЫЙ ОГОНЬ!!!', 14, 0, 100, 0, 0, 46114, 82206, 62000, 200000, 21912, 'Нитрогг Громовая Гора to Осадная пушка');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Зададим им жару!', 14, 0, 100, 0, 0, 46111, 82204, 34000, 151000, 21912, 'Нитрогг Громовая Гора to Гром\'карский подрывник');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Не щадите их!', 14, 0, 100, 0, 0, 46112, 82207, 121000, 225000, 0, 'Нитрогг Громовая Гора to Гром\'карский гренадер');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Одним соперником меньше!', 14, 0, 100, 0, 0, 46120, 82209, 114000, 114000, 0, 'Нитрогг Громовая Гора to Осадная пушка');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Размазня!', 14, 0, 100, 0, 0, 46121, 109511, 4000, 4000, 0, 'Нитрогг Громовая Гора to Нитрогг Громовая Гора');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Со мной никто не сравнится!', 14, 0, 100, 0, 0, 46122, 82211, 6000, 6000, 161153, 'Нитрогг Громовая Гора to Нитрогг Громовая Гора');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Ты у меня на прицеле.', 14, 0, 100, 0, 0, 46113, 82205, 47000, 216000, 21912, 'Нитрогг Громовая Гора to Осадная пушка');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Ха! Спущусь до вашего уровня…', 14, 0, 100, 0, 0, 46115, 82208, 230000, 230000, 0, 'Нитрогг Громовая Гора to Осадная пушка');
INSERT INTO `creature_text` VALUES (79545, 4, 0, 'Я бился... вполсилы...', 14, 0, 100, 0, 0, 46110, 82212, 260000, 260000, 0, 'Нитрогг Громовая Гора to Player');
INSERT INTO `creature_text` VALUES (79559, 0, 0, '|TInterface\\Icons\\INV_Fabric_Felcloth_Ebon.blp:20|t |cFFFF0404|Hspell:161242|h[Жгучая энергия]|h|r перетекает в |3-1($n).', 41, 0, 100, 0, 0, 41973, 88261, 0, 0, 0, 'Руна нейтрализации to Player');
INSERT INTO `creature_text` VALUES (79567, 0, 0, 'With the giant defeated we shouldn\'t have any more trouble clearing out the area.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (79567, 0, 0, 'Теперь, когда монстр пал, мы легко зачистим остальную территорию.', 12, 0, 100, 1, 0, 45611, 81884, 0, 0, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (79567, 1, 0, 'Killing the beast will surely gain the respect of Karabor peacekeepers.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (79576, 0, 0, 'Как только разберемся с ограми, Пылающим Клинком и орками Лок\'рата, сразу двинемся на Громмашар.', 12, 0, 100, 1, 0, 46252, 76797, 0, 0, 0, 'Рангари Д\'каан to Player');
INSERT INTO `creature_text` VALUES (79576, 1, 0, 'Удачи, командир.', 12, 0, 100, 1, 0, 46255, 83542, 0, 0, 0, 'Рангари Д\'каан to Player');
INSERT INTO `creature_text` VALUES (79581, 1, 0, 'Падите перед мощью Железной Орды!', 12, 0, 100, 92, 0, 0, 82019, 0, 0, 0, 'Железный страж to Player');
INSERT INTO `creature_text` VALUES (79581, 1, 0, 'Пусть я паду – еще десять встанут на мое место!', 12, 0, 100, 0, 0, 0, 82015, 0, 0, 0, 'Железный страж to Player');
INSERT INTO `creature_text` VALUES (79581, 1, 0, 'Умри, ничтожество!', 12, 0, 100, 0, 0, 0, 133408, 0, 0, 22120, 'Железный страж to Player');
INSERT INTO `creature_text` VALUES (79581, 3, 0, 'За Железную Орду!', 12, 0, 100, 0, 0, 0, 82013, 836000, 836000, 22120, 'Железный страж to Награндский волк');
INSERT INTO `creature_text` VALUES (79583, 1, 0, 'Вторжение! Кто-нибудь, доложите Кели\'дану!', 14, 0, 100, 0, 0, 0, 89984, 0, 0, 0, 'Унгра to Player');
INSERT INTO `creature_text` VALUES (79584, 1, 0, 'За Железную Орду!', 12, 0, 100, 0, 0, 0, 82013, 0, 0, 0, 'Железный арбалетчик to Player');
INSERT INTO `creature_text` VALUES (79584, 1, 0, 'Падите перед мощью Железной Орды!', 12, 0, 100, 0, 0, 0, 82019, 0, 0, 0, 'Железный арбалетчик to Player');
INSERT INTO `creature_text` VALUES (79584, 1, 0, 'Пусть я паду – еще десять встанут на мое место!', 12, 0, 100, 0, 0, 0, 82015, 0, 0, 0, 'Железный арбалетчик to Player');
INSERT INTO `creature_text` VALUES (79584, 1, 0, 'Умри, ничтожество!', 12, 0, 100, 0, 0, 0, 133408, 0, 0, 0, 'Железный арбалетчик to Player');
INSERT INTO `creature_text` VALUES (79585, 1, 0, 'Я буду пировать вашими душами...', 14, 0, 100, 0, 0, 0, 89985, 0, 0, 0, 'Надсмотрщик Гурран to Player');
INSERT INTO `creature_text` VALUES (79586, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 10000, 10000, 0, 'Железный батрак to Player');
INSERT INTO `creature_text` VALUES (79588, 0, 0, 'Величие Железной Орды не сравнимо ни с чем! Каждый обед – это пир, каждый налет сулит богатую добычу! Нас не остановить, а наша слава будет вечной!', 14, 0, 100, 0, 0, 0, 81702, 0, 0, 0, 'Генерал Кулл\'крош');
INSERT INTO `creature_text` VALUES (79588, 0, 0, 'Возможно, вы боитесь дыма и жара наших горнов. Я говорю вам, отбросьте страх! Наша броня несокрушима! Наши клинки поразят любого врага! Огни наших мастерских сделают вас величайшими воинами на свете!', 14, 0, 100, 0, 0, 0, 81722, 0, 0, 0, 'Генерал Кулл\'крош');
INSERT INTO `creature_text` VALUES (79588, 0, 0, 'Пример Песни Войны воистину вдохновляют! Вы взяли Награнд за горло и завладели им! Железная Орда почтет за честь принять клан Песни Войны в свои ряды. Пусть Дренор трепещет перед Песнью Войны, побеждающей во славу Железной Орды!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Генерал Кулл\'крош');
INSERT INTO `creature_text` VALUES (79588, 0, 0, 'С Громмашем мы одержим множество славных побед в ОБОИХ мирах. Разве мы можем потерпеть поражение, когда нас ведет Песня Войны? Союз Песни Войны и Железной Орды непобедим!', 14, 0, 100, 0, 0, 0, 81704, 0, 0, 0, 'Генерал Кулл\'крош');
INSERT INTO `creature_text` VALUES (79588, 0, 0, 'Сражаться за Железную Орду – вот истинная слава! Враги Железной Орды сильны и опасны. Но их незачем бояться! Опасные враги лишь делают нас сильнее: они могут нас ранить, но победить – никогда. Нас не сломить!', 14, 0, 100, 0, 0, 0, 81723, 0, 0, 0, 'Генерал Кулл\'крош');
INSERT INTO `creature_text` VALUES (79588, 1, 0, 'Ваши усилия тщетны! Скоро тебе и твоим союзникам конец!', 12, 0, 100, 0, 0, 0, 82026, 0, 0, 0, 'Генерал Кулл\'крош to Player');
INSERT INTO `creature_text` VALUES (79588, 1, 0, 'Думаешь, Песня Войны изменит решение, если ты сразишь меня? Я уже сделал свое дело: клан Песни Войны стал частью Железной Орды!', 12, 0, 100, 0, 0, 0, 82027, 0, 0, 0, 'Генерал Кулл\'крош to Player');
INSERT INTO `creature_text` VALUES (79588, 1, 0, 'Убив тебя, я ускорю вербовку раз в десять – и наверняка даже доспех не поцарапаю!', 12, 0, 100, 0, 0, 0, 82028, 0, 0, 0, 'Генерал Кулл\'крош to Player');
INSERT INTO `creature_text` VALUES (79588, 2, 0, 'Железная Орда продолжит сражаться и без меня!', 12, 0, 100, 0, 0, 0, 87432, 0, 0, 0, 'Генерал Кулл\'крош to Player');
INSERT INTO `creature_text` VALUES (79588, 3, 0, 'Верно говорят, что на полях Награнда враги бесчисленны, а слава безгранична. Но ваш вождь решил, что вы заслуживаете еще большего – вы отправитесь с ним в завоевательный поход по всему Дренору. Железная Орда обретет подлинное могущество в обоих мирах!', 14, 0, 100, 25, 0, 0, 81705, 605000, 605000, 0, 'Генерал Кулл\'крош');
