INSERT INTO `creature_text` VALUES (79754, 1, 0, 'Пусть я паду – еще десять встанут на мое место!', 12, 0, 100, 0, 0, 0, 82015, 0, 0, 0, 'Новобранец клана Песни Войны to Player');
