INSERT INTO `creature_text` VALUES (79754, 3, 0, 'За Железную Орду!', 12, 0, 100, 0, 0, 0, 82013, 312000, 312000, 0, 'Новобранец клана Песни Войны to Player');
INSERT INTO `creature_text` VALUES (79758, 3, 0, 'I just want to pick up my daughter and leave. We are not your enemies.', 12, 7, 100, 0, 0, 11819, 81739, 194000, 194000, 0, 'Корчмарь Фелаана');
INSERT INTO `creature_text` VALUES (79758, 3, 0, 'Вы просто чудовища! Угрожаете беззащитной женщине с ребенком!', 12, 7, 100, 0, 0, 11819, 81742, 475609, 475609, 0, 'Хозяйка таверны Фелаана');
INSERT INTO `creature_text` VALUES (79758, 3, 0, 'Могу я поговорить с вашим командиром? Объяснить все? Он отпустит нас, увидите!', 12, 7, 100, 0, 0, 0, 81740, 15099, 15099, 0, 'Хозяйка таверны Фелаана');
INSERT INTO `creature_text` VALUES (79758, 3, 0, 'Отпустите нас! Мы никому не причинили зла.', 12, 7, 100, 0, 0, 0, 81736, 95000, 95000, 0, 'Корчмарь Фелаана');
INSERT INTO `creature_text` VALUES (79758, 3, 0, 'Отпустите хотя бы Изель. Она же совсем ребенок!', 12, 7, 100, 0, 0, 11819, 81737, 41000, 41000, 0, 'Корчмарь Фелаана');
INSERT INTO `creature_text` VALUES (79758, 3, 0, 'Прошу вас, умоляю. Отпустите нас!', 12, 7, 100, 0, 0, 11819, 81743, 7000, 7000, 0, 'Корчмарь Фелаана');
INSERT INTO `creature_text` VALUES (79758, 3, 0, 'Только троньте моего ребенка! Я убью вас!', 12, 7, 100, 0, 0, 0, 81741, 76000, 76000, 0, 'Корчмарь Фелаана');
INSERT INTO `creature_text` VALUES (79762, 0, 0, 'Если вы тронули мелкую или ее мать, я вас убью, а потом еще раз убью!', 12, 0, 100, 35, 0, 0, 81795, 0, 0, 0, 'Гензель Большерук to Налетчик из клана Песни Войны');
INSERT INTO `creature_text` VALUES (79762, 1, 0, 'Кого убить первым?', 12, 0, 100, 35, 0, 0, 81790, 0, 0, 0, 'Гензель Большерук to Налетчик из клана Песни Войны');
INSERT INTO `creature_text` VALUES (79762, 2, 0, 'Я их там потрепал немного.', 12, 0, 100, 0, 0, 0, 81806, 0, 0, 0, 'Гензель Большерук to Player');
INSERT INTO `creature_text` VALUES (79762, 3, 0, 'Что-то меня шатает… Справишься? Я пойду обратно на станцию.', 12, 0, 100, 0, 0, 0, 81807, 0, 0, 0, 'Гензель Большерук to Player');
INSERT INTO `creature_text` VALUES (79766, 0, 0, 'О, хвала Свету за то, что он послал нам тебя!', 12, 7, 100, 2, 0, 11819, 81755, 0, 0, 0, 'Корчмарь Фелаана to Player');
INSERT INTO `creature_text` VALUES (79766, 1, 0, 'Клан Песни Войны напал на нас совершенно неожиданно.', 12, 7, 100, 273, 0, 0, 81756, 0, 0, 0, 'Корчмарь Фелаана to Player');
INSERT INTO `creature_text` VALUES (79766, 2, 0, 'Идем, Изель. Только тихо, нам нельзя шуметь.', 12, 7, 100, 1, 0, 0, 81757, 0, 0, 0, 'Корчмарь Фелаана to Player');
INSERT INTO `creature_text` VALUES (79768, 0, 0, 'Да, мама. Спасибо, $gдяденька:тетенька;!', 12, 7, 100, 1, 0, 0, 81759, 0, 0, 0, 'Изель to Корчмарь Фелаана');
INSERT INTO `creature_text` VALUES (79770, 3, 0, 'Что-то меня шатает… Справишься? Я пойду обратно на станцию.', 12, 7, 100, 6, 0, 0, 81807, 6000, 6000, 0, 'Гензель Большерук to Player');
INSERT INTO `creature_text` VALUES (79770, 3, 0, 'Я их там потрепал немного.', 12, 7, 100, 1, 0, 0, 81806, 0, 0, 166665, 'Гензель Большерук to Player');
INSERT INTO `creature_text` VALUES (79774, 0, 0, 'Воздух наполнен смрадом. Это демоны…', 12, 0, 100, 0, 0, 0, 90376, 0, 0, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (79774, 1, 0, 'Совет Теней? Демоны! Поднять тревогу!', 12, 0, 100, 0, 0, 0, 88391, 0, 0, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (79774, 2, 0, 'Удача на нашей стороне! И вновь мы покажем демонам, что истинную Орду никто не подомнет под себя.', 14, 0, 100, 22, 0, 0, 90377, 0, 0, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (79774, 3, 0, 'Пусть темное небо не страшит тебя. Сегодня хороший день.', 12, 0, 100, 0, 0, 0, 91624, 0, 0, 0, 'Сержант Зловещая Утроба to 0');
INSERT INTO `creature_text` VALUES (79774, 4, 0, 'Покажем запятнанному демонической порчей Совету Теней, что значит сила свободы!', 12, 0, 100, 0, 0, 0, 91625, 0, 0, 0, 'Сержант Зловещая Утроба to 0');
INSERT INTO `creature_text` VALUES (79774, 5, 0, 'Мы просто так не сдадимся!', 14, 0, 100, 0, 0, 0, 90435, 0, 0, 0, 'Сержант Зловещая Утроба to 0');
INSERT INTO `creature_text` VALUES (79774, 6, 0, 'Рассредоточьтесь! Не дайте им закрепиться!', 14, 0, 100, 0, 0, 0, 90436, 0, 0, 0, 'Сержант Зловещая Утроба to 0');
INSERT INTO `creature_text` VALUES (79774, 7, 0, 'Кровь и честь!', 14, 0, 100, 0, 0, 0, 101408, 0, 0, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (79774, 11, 0, 'Вытесняйте их! Ни шагу назад!', 14, 0, 100, 0, 0, 0, 91855, 0, 0, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (79774, 13, 0, 'Им не хватает мозгов, чтобы понять, что они побеждены!', 14, 0, 100, 0, 0, 0, 90406, 0, 0, 0, 'Сержант Зловещая Утроба to 0');
INSERT INTO `creature_text` VALUES (79774, 14, 0, 'Давайте освободим эти демонические отродья от мучений!', 14, 0, 100, 0, 0, 0, 90399, 0, 0, 0, 'Сержант Зловещая Утроба to 0');
INSERT INTO `creature_text` VALUES (79774, 16, 0, 'За Орду!', 14, 0, 100, 0, 0, 0, 143151, 0, 0, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (79776, 3, 0, 'Сержант, нас атакует Совет Теней!', 12, 0, 100, 0, 0, 0, 88388, 5000, 5000, 0, 'Разведчик Ледяных скал to Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (79776, 3, 0, 'Сержант, орки Громоборцев у наших стен!', 12, 0, 100, 0, 0, 0, 81914, 5000, 5000, 0, 'Разведчик Ледяных скал to Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (79776, 3, 0, 'Сержант! На горизонте дым, слышны боевые барабаны!', 12, 0, 100, 0, 0, 0, 86681, 6000, 6000, 0, 'Разведчик Ледяных скал to Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (79781, 0, 0, 'Их атака захлебывается.', 12, 0, 100, 0, 0, 0, 83929, 0, 0, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 0, 0, 'Наконец-то можно как следует подраться.', 12, 0, 100, 0, 0, 0, 84188, 0, 0, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 0, 0, 'Они собираются напасть на наши здания!', 12, 0, 100, 0, 0, 0, 83924, 0, 0, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 3, 0, 'They\'re on the run now.', 12, 0, 100, 0, 0, 42439, 83927, 724109, 724109, 0, 'Frostwall Grunt to Frostwall Grunt');
INSERT INTO `creature_text` VALUES (79781, 3, 0, 'To arms! For the Horde!', 12, 0, 100, 0, 0, 42439, 84187, 258625, 258625, 0, 'Frostwall Grunt to Frostwall Grunt');
INSERT INTO `creature_text` VALUES (79781, 3, 0, 'К оружию! За Орду!', 12, 0, 100, 0, 0, 8333, 84187, 77000, 77000, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 3, 0, 'Уведи батраков в безопасное место.', 12, 0, 100, 0, 0, 0, 84189, 428000, 428000, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 3, 0, 'Устанешь тут считать. Сколько уже?', 12, 0, 100, 0, 0, 0, 85716, 136000, 136000, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 4, 0, 'Strike their hearts!', 12, 0, 100, 0, 0, 42439, 81065, 27282, 27282, 158600, 'Frostwall Grunt to Thunderlord Giantslayer');
INSERT INTO `creature_text` VALUES (79781, 4, 0, 'They must breed like Murlocs.', 12, 0, 100, 0, 0, 42439, 85717, 22125, 22125, 0, 'Frostwall Grunt to Frostwall Grunt');
INSERT INTO `creature_text` VALUES (79781, 4, 0, 'Бей в сердце!', 12, 0, 100, 0, 0, 0, 81065, 54000, 54000, 158600, 'Рубака Ледяных скал to Копьеносец из клана Громоборцев');
INSERT INTO `creature_text` VALUES (79781, 4, 0, 'Они бегут.', 12, 0, 100, 0, 0, 0, 83927, 29360, 29360, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 4, 0, 'Они слабеют.', 12, 0, 100, 0, 0, 0, 87709, 17000, 17000, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79781, 4, 0, 'Они, должно быть, плодятся как мурлоки.', 12, 0, 100, 0, 0, 0, 85717, 26000, 26000, 0, 'Рубака Ледяных скал to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (79783, 4, 0, 'Мы в твоем распоряжении.', 12, 0, 100, 1, 0, 44135, 120509, 11747, 11747, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (79795, 0, 0, 'Надо торопиться - орками Призрачной Луны командует некий Нер\'зул.', 12, 0, 100, 396, 0, 45552, 81784, 0, 0, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (79795, 1, 0, 'После гибели своих командиров, Нер\'зул начнет искать нас.', 12, 0, 100, 396, 0, 45553, 81785, 0, 0, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (79795, 2, 0, 'О, я вижу наверху твои друзья? Думаю, я смогу добраться туда.', 12, 0, 100, 0, 0, 45554, 85240, 0, 0, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (79796, 3, 0, 'Are you going to be okay?', 12, 0, 100, 0, 0, 0, 81798, 28094, 28094, 0, 'Draenei Refugee');
INSERT INTO `creature_text` VALUES (79796, 3, 0, 'Точно все в порядке?', 12, 0, 100, 71, 0, 0, 81798, 1000, 1000, 0, 'Дренейка-беженка');
INSERT INTO `creature_text` VALUES (79799, 3, 0, 'Ничего тут не высоко. Давай, прыгай уже!', 14, 0, 100, 1, 0, 45391, 81804, 40000, 40000, 0, 'Дреней-беженец');
INSERT INTO `creature_text` VALUES (79814, 3, 0, 'What can I do for you?', 12, 0, 100, 0, 0, 42439, 147196, 2266, 2266, 0, 'Keyana Tone to Keyana Tone');
INSERT INTO `creature_text` VALUES (79824, 1, 0, 'Тьма идет за тобой...', 12, 0, 100, 0, 0, 0, 81679, 0, 0, 0, 'Страж Бездны из клана Призрачной Луны to Player');
INSERT INTO `creature_text` VALUES (79839, 0, 0, 'Пленных больше не берем. Чужаков убивать на месте!', 14, 0, 100, 0, 0, 0, 81847, 0, 0, 0, 'Предводительница стаи Каргора to 0');
INSERT INTO `creature_text` VALUES (79839, 1, 0, 'Найдите лазутчика и этих проклятых рангари.', 14, 0, 100, 15, 0, 0, 81845, 0, 0, 0, 'Предводительница стаи Каргора to 0');
INSERT INTO `creature_text` VALUES (79839, 2, 0, 'Быстрее! Я намереваюсь вскоре атаковать Телаарскую станцию.', 14, 0, 100, 15, 0, 0, 81850, 0, 0, 0, 'Предводительница стаи Каргора to 0');
INSERT INTO `creature_text` VALUES (79839, 3, 0, 'У меня есть то, что тебе нужно, командир. Приди и возьми. Песня Войны! Разыскать $gего:ее;!', 14, 0, 100, 0, 0, 0, 81838, 0, 0, 0, 'Предводительница стаи Каргора to Player');
INSERT INTO `creature_text` VALUES (79839, 4, 0, 'Ха-ха-ха-ха! Приди и возьми свои драгоценные чертежи. Если сможешь!', 14, 0, 100, 0, 0, 0, 81839, 0, 0, 0, 'Предводительница стаи Каргора to Player');
INSERT INTO `creature_text` VALUES (79839, 5, 0, 'Клан Песни Войны! Ко мне!', 14, 0, 100, 0, 0, 0, 85481, 0, 0, 0, 'Предводительница стаи Каргора to 0');
INSERT INTO `creature_text` VALUES (79839, 6, 0, 'Клан Песни Войны разорвет тебя на части!', 14, 0, 100, 0, 0, 0, 85484, 0, 0, 0, 'Предводительница стаи Каргора to 0');
INSERT INTO `creature_text` VALUES (79847, 3, 0, 'I will not jump! Bring the ladder!', 14, 0, 100, 274, 0, 0, 81801, 399000, 399000, 0, 'Дренейка-беженка to Дреней-беженец');
INSERT INTO `creature_text` VALUES (79847, 3, 0, 'No way! It\'s too far! Seriously... get a ladder!', 14, 0, 100, 274, 0, 0, 81802, 72360, 72360, 0, 'Draenei Refugee to Draenei Refugee');
INSERT INTO `creature_text` VALUES (79847, 3, 0, 'Нет! Слишком высоко! Ну правда, тащи лестницу!', 14, 0, 100, 274, 0, 0, 81802, 56000, 349000, 0, 'Дренейка-беженка to Дреней-беженец');
INSERT INTO `creature_text` VALUES (79890, 0, 0, 'Yes, yes, I understand. I will tell you the way.', 12, 0, 100, 396, 0, 46230, 82310, 0, 0, 0, 'Ornekka to Player');
INSERT INTO `creature_text` VALUES (79890, 1, 0, 'And I see you out there, Reshad! No use hiding from me, darling. Come in, come in!', 12, 0, 100, 70, 0, 46231, 82311, 0, 0, 0, 'Ornekka to Player');
INSERT INTO `creature_text` VALUES (79890, 2, 0, 'You too, Percy! That\'s it... give old Ornekka some sugar.', 12, 0, 100, 153, 0, 46232, 82312, 0, 0, 0, 'Ornekka to Player');
INSERT INTO `creature_text` VALUES (79890, 3, 0, 'Oh, how sweet of you, Reshad! I never knew you cared.', 12, 0, 100, 11, 0, 46233, 84646, 0, 0, 0, 'Ornekka to Player');
INSERT INTO `creature_text` VALUES (79890, 4, 0, 'But my old bones would only slow you down. I\'ll be fine here.', 12, 0, 100, 1, 0, 46234, 84647, 0, 0, 0, 'Ornekka to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'I helped! Seen, seen?', 12, 0, 100, 0, 0, 0, 81892, 160016, 160016, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Моя прелесть...', 12, 0, 100, 0, 0, 0, 81951, 205844, 205844, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Научишь меня так сражаться?', 12, 0, 100, 0, 0, 0, 81898, 106453, 106453, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Не торопись, я буду здесь... ждать... рядом с тобой... вечно.', 12, 0, 100, 0, 0, 0, 81937, 65000, 65000, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'От тебя так много зависит! Ты даже представить не можешь всех последствий!', 12, 0, 100, 0, 0, 0, 81939, 95000, 95000, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Проверь, есть ли там что-нибудь ценное!', 12, 0, 100, 0, 0, 0, 81896, 10969, 10969, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Прошу! Ты $gдолжен:должна; мне помочь!', 12, 0, 100, 0, 0, 0, 81934, 24000, 24000, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Разделим добычу! Мне три пятых, тебе две, идет?', 12, 0, 100, 0, 0, 0, 81894, 62406, 62406, 0, 'Базвикс to Player');
INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Следующий – мой! Гляди!', 12, 0, 100, 0, 0, 0, 81899, 42391, 42391, 0, 'Базвикс to Player');
