INSERT INTO `creature_text` VALUES (79899, 3, 0, 'Я вообще-то могу о себе позаботиться, но мне нужна твоя помощь, чтобы вернуть мои пожитки!', 12, 0, 100, 0, 0, 0, 81936, 50000, 50000, 0, 'Базвикс to Player');
