INSERT INTO `creature_text` VALUES (80059, 3, 0, 'Ну все, $p тебе сейчас вмажет!', 12, 0, 100, 21, 0, 0, 82048, 30000, 30000, 0, 'Работник форта Ринн to Работник форта Ринн');
INSERT INTO `creature_text` VALUES (80059, 3, 0, 'Обидишь крестьянина – получишь от командира!', 12, 0, 100, 1, 0, 0, 82042, 13000, 13000, 161564, 'Работник форта Ринн to Работник форта Ринн');
INSERT INTO `creature_text` VALUES (80059, 3, 0, 'Спасибо, командир! Я вернусь к работе!', 12, 0, 100, 1, 0, 0, 82039, 20000, 20000, 0, 'Работник форта Ринн to Кишкодер');
