INSERT INTO `creature_text` VALUES (80073, 2, 0, 'Это моя обязанность. Позвольте мне поговорить с умершим.', 12, 0, 100, 274, 0, 44585, 84976, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (80073, 3, 0, 'Среди нас предатель. Я найду его!', 12, 0, 100, 5, 0, 44586, 84979, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (80075, 0, 0, 'It\'s working! It\'s... not stable. But it\'s working.', 12, 0, 100, 0, 0, 46747, 84066, 0, 0, 0, 'Exarch Hataaru to 0');
INSERT INTO `creature_text` VALUES (80075, 1, 0, 'This new structure is able to tap into a surprising amount of power.', 12, 0, 100, 0, 0, 46748, 84067, 0, 0, 0, 'Exarch Hataaru to 0');
INSERT INTO `creature_text` VALUES (80075, 2, 0, 'Defend the ward at all costs!', 12, 0, 100, 0, 0, 46749, 84068, 0, 0, 0, 'Exarch Hataaru to 0');
INSERT INTO `creature_text` VALUES (80075, 3, 0, 'Отвлеките их! Кристалл черпает слишком много энергии. ', 12, 0, 100, 0, 0, 46752, 84071, 0, 0, 0, 'Экзарх Хатаару to 0');
INSERT INTO `creature_text` VALUES (80076, 0, 0, 'Exarch Naielle, weren\'t you overheard arguing with Hataaru earlier?', 12, 0, 100, 6, 0, 43278, 84972, 0, 0, 0, 'Exarch Othaar to Player');
INSERT INTO `creature_text` VALUES (80076, 1, 0, 'Ahh, it\'s you. Trust me, friend, I will handle this. You may go.', 12, 0, 100, 0, 0, 43281, 84620, 0, 0, 0, 'Exarch Othaar to Player');
INSERT INTO `creature_text` VALUES (80076, 2, 0, 'What is that? Is that Maladaar\'s work? No no, you don\'t need to do that. He is not to be trusted.', 12, 0, 100, 6, 0, 43282, 84625, 0, 0, 0, 'Exarch Othaar to Player');
INSERT INTO `creature_text` VALUES (80076, 3, 0, 'Damn him. I thought I\'d gotten rid of Maladaar when I gave him up to the Iron Horde.', 12, 0, 100, 0, 0, 43283, 84694, 0, 0, 0, 'Exarch Othaar to Player');
INSERT INTO `creature_text` VALUES (80076, 4, 0, 'Yes, I had Hataaru killed. And you are next.', 12, 0, 100, 0, 0, 43284, 84695, 0, 0, 0, 'Exarch Othaar to 0');
INSERT INTO `creature_text` VALUES (80076, 5, 0, 'I am not the traitor here. It was the false prophet Velen who betrayed our people.', 12, 0, 100, 0, 0, 43285, 84731, 0, 0, 0, 'Exarch Othaar to 0');
INSERT INTO `creature_text` VALUES (80076, 6, 0, 'I serve our true master now. The Legion whose call I once denied.', 12, 0, 100, 6, 0, 43286, 84736, 0, 0, 0, 'Exarch Othaar to 0');
INSERT INTO `creature_text` VALUES (80076, 7, 0, 'My Lord Sargeras will scorch this pathetic world, as he has countless others.', 12, 0, 100, 5, 0, 43287, 84738, 0, 0, 0, 'Exarch Othaar to 0');
INSERT INTO `creature_text` VALUES (80076, 8, 0, 'I have sworn the sacrament. I have taken my new name... Socrethar.', 12, 0, 100, 1, 0, 43289, 84742, 0, 0, 0, 'Exarch Othaar to 0');
INSERT INTO `creature_text` VALUES (80076, 9, 0, 'There are others at my command. In Shattrath and Auchindoun.', 12, 0, 100, 0, 0, 43291, 84759, 0, 0, 0, 'Socrethar to 0');
INSERT INTO `creature_text` VALUES (80076, 10, 0, 'Enough! When next we meet, it will be on MY terms.', 12, 0, 100, 1, 0, 43292, 84760, 0, 0, 0, 'Socrethar to 0');
INSERT INTO `creature_text` VALUES (80076, 11, 0, 'Tell the remaining exarchs to number their days.', 12, 0, 100, 0, 0, 43293, 84761, 0, 0, 0, 'Socrethar to 0');
INSERT INTO `creature_text` VALUES (80076, 12, 0, 'А ты, похоже, много знаешь об орудиях врага.', 12, 0, 100, 1, 0, 43279, 84975, 0, 0, 0, 'Exarch Othaar to 0');
INSERT INTO `creature_text` VALUES (80076, 13, 0, 'I agree. Stand down, Exarch. Enough damage has been done.', 12, 0, 100, 1, 0, 43280, 84978, 0, 0, 0, 'Exarch Othaar to 0');
INSERT INTO `creature_text` VALUES (80078, 0, 0, 'Ah. The little one is the taskmaster.', 12, 0, 100, 1, 0, 46713, 83650, 0, 0, 0, 'Exarch Akama to Player');
INSERT INTO `creature_text` VALUES (80078, 1, 0, 'Commander! You have built a most impressive garrison. I am eager to see your men in action.', 12, 0, 100, 2, 0, 46714, 83645, 0, 0, 0, 'Exarch Akama to Player');
INSERT INTO `creature_text` VALUES (80078, 2, 0, 'This is how he was found. Murdered!', 12, 0, 100, 1, 0, 46725, 84971, 0, 0, 0, 'Exarch Akama to Player');
INSERT INTO `creature_text` VALUES (80078, 3, 0, 'Получилось.', 12, 0, 100, 1, 0, 46718, 83882, 0, 0, 0, 'Экзарх Акама to Воздаятель Мараад');
INSERT INTO `creature_text` VALUES (80078, 4, 0, 'Его нашли здесь. Это убийство!', 12, 0, 100, 1, 0, 46725, 84971, 0, 0, 0, 'Экзарх Акама to Player');
INSERT INTO `creature_text` VALUES (80078, 5, 0, 'Нет! Он и так уже настрадался!', 12, 0, 100, 5, 0, 46726, 84977, 0, 0, 0, 'Экзарх Акама to Player');
INSERT INTO `creature_text` VALUES (80078, 6, 0, 'Ищи на северо-западе. Я пойду следом.', 12, 0, 100, 1, 0, 46715, 83897, 0, 0, 0, 'Экзарх Акама to Player');
INSERT INTO `creature_text` VALUES (80079, 3, 0, 'Мы всегда спорили! Но я его не убивала.', 12, 0, 100, 5, 0, 45891, 84973, 95000, 95000, 0, 'Экзарх Найель to Player');
INSERT INTO `creature_text` VALUES (80080, 1, 0, 'Не видишь – я развлекаю \"почетного\" гостя!', 12, 0, 100, 0, 0, 0, 82093, 0, 0, 0, 'Болкар Жестокий to Player');
INSERT INTO `creature_text` VALUES (80080, 3, 0, 'Что такое, котеночек? Кнут не по вкусу?', 14, 0, 100, 0, 0, 0, 82077, 3834122, 3860103, 0, 'Болкар Жестокий to Player');
INSERT INTO `creature_text` VALUES (80080, 4, 0, 'Больно... невыносимо больно...', 12, 0, 100, 0, 0, 0, 87430, 21598, 21598, 0, 'Болкар Жестокий to Player');
INSERT INTO `creature_text` VALUES (80087, 0, 0, 'Ты и я – против целого клана? Это мне по нраву.', 12, 0, 100, 396, 0, 46156, 83856, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (80087, 1, 0, 'С помощью магии крови орки Кровавой Глазницы превращают воинов в огромных берсерков.', 12, 0, 100, 396, 0, 46157, 83884, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (80087, 2, 0, 'Если повезет, мы уберемся отсюда раньше, чем они завершат ритуал.', 12, 0, 100, 396, 0, 46158, 83888, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (80087, 3, 0, 'Мы здесь, трусы! Сюда! Бейтесь с нами!', 14, 0, 100, 0, 0, 46159, 83857, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (80087, 4, 0, 'Как бы их отвлечь от ритуала… Давай уничтожим кровавые сферы.', 12, 0, 100, 0, 0, 0, 85454, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (80087, 5, 0, 'Похоже, Кадгар и все остальные уже в пути. Наша работа почти завершена!', 12, 0, 100, 0, 0, 46162, 83861, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (80108, 0, 0, 'This way, friend. The ward is nearly at full power.', 12, 0, 100, 0, 0, 46759, 82095, 0, 0, 0, 'Exarch Hataaru to Player');
INSERT INTO `creature_text` VALUES (80108, 1, 0, 'We will sink these crystalline resonance emitters into the earth, harnessing the very energies of Draenor itself to power our defenses.', 12, 0, 100, 396, 0, 46760, 82096, 0, 0, 0, 'Exarch Hataaru to Player');
INSERT INTO `creature_text` VALUES (80108, 2, 0, 'On our homeworld, art, science, magic and technology were all as one. Ah, such wonders we would build!', 12, 0, 100, 396, 0, 46761, 83946, 0, 0, 0, 'Exarch Hataaru to Player');
INSERT INTO `creature_text` VALUES (80108, 3, 0, 'Now, all I have is spare parts and whatever primitive materials we can cobble together.', 12, 0, 100, 396, 0, 46762, 83947, 0, 0, 0, 'Exarch Hataaru to Player');
INSERT INTO `creature_text` VALUES (80108, 4, 0, 'It is ready! Go inside and power on the console while I observe...', 12, 0, 100, 396, 0, 46763, 83948, 0, 0, 0, 'Exarch Hataaru to Player');
INSERT INTO `creature_text` VALUES (80138, 3, 0, 'Леди Праудмур запретила Орде ступать на территорию Кирин-Тора.', 12, 0, 100, 1, 0, 45993, 82116, 7861, 7861, 0, 'Привратник Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (80140, 0, 0, 'Я могу начать постройку, но без чертежей мне ее не закончить. Они – ключ ко всему, что мы тут делаем.', 12, 0, 100, 274, 0, 0, 82227, 0, 0, 0, 'Штейгер Тазз\'рил to Player');
INSERT INTO `creature_text` VALUES (80140, 1, 0, 'Это место годится для строительства стойл или танковой мастерской. Выбирай, а об остальном я позабочусь.', 12, 0, 100, 1, 0, 0, 83746, 0, 0, 0, 'Штейгер Тазз\'рил to Player');
INSERT INTO `creature_text` VALUES (80145, 0, 0, 'Идем, я познакомлю тебя кое с кем, кто поможет тебе построить башню.', 12, 0, 100, 1, 0, 44904, 82109, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (80145, 3, 0, 'Страж врат, пропусти нас.', 12, 0, 100, 1, 0, 44905, 82112, 23000, 23000, 161702, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (80153, 0, 0, 'Let the prisoner go. Its master has earned a favor from us.', 14, 0, 100, 1, 0, 0, 83846, 0, 0, 0, 'Shadow-Sage Iskar to Player');
