INSERT INTO `creature_text` VALUES (80155, 0, 0, 'The sol-shapers will be severely weakened in those caves, but do not underestimate them!', 12, 0, 100, 0, 0, 0, 84110, 0, 0, 0, 'Kura the Blind to Player');
INSERT INTO `creature_text` VALUES (80161, 0, 0, 'Если хочешь знать, где твоя важная бумажка, то возвращайся с моим клинком или вообще не возвращайся.', 12, 0, 100, 1, 0, 0, 82328, 0, 0, 0, 'Лантрезор Клинок to Player');
