INSERT INTO `creature_text` VALUES (80162, 1, 0, 'Another prisoner for the sacrifice!', 12, 0, 100, 0, 0, 0, 88829, 0, 0, 0, 'Shadowmoon Voidmancer to Player');
INSERT INTO `creature_text` VALUES (80162, 1, 0, 'I will deliver you to your end!', 12, 0, 100, 0, 0, 0, 88831, 0, 0, 0, 'Shadowmoon Voidmancer to Player');
INSERT INTO `creature_text` VALUES (80162, 1, 0, 'Вот еще один узник для жертвоприношения!', 12, 0, 100, 0, 0, 0, 88829, 0, 0, 0, 'Маг Бездны из клана Призрачной Луны to Player');
INSERT INTO `creature_text` VALUES (80162, 1, 0, 'Мы накроем этот мир покрывалом вечной тьмы!', 12, 0, 100, 0, 0, 0, 88828, 0, 0, 0, 'Маг Бездны из клана Призрачной Луны to Player');
INSERT INTO `creature_text` VALUES (80162, 1, 0, 'Нер\'зула нельзя прерывать!', 12, 0, 100, 0, 0, 0, 77739, 0, 0, 0, 'Маг Бездны из клана Призрачной Луны');
INSERT INTO `creature_text` VALUES (80162, 1, 0, 'Я лично прикончу тебя!', 12, 0, 100, 0, 0, 0, 88831, 0, 0, 0, 'Маг Бездны из клана Призрачной Луны to Player');
INSERT INTO `creature_text` VALUES (80162, 3, 0, 'Мы исполняем волю Нер\'зула!', 12, 0, 100, 0, 0, 0, 88827, 7000, 7000, 0, 'Маг Бездны из клана Призрачной Луны to Телуунский стражник');
INSERT INTO `creature_text` VALUES (80162, 3, 0, 'Тьма – наша воля, и железо – наша кровь!', 12, 0, 100, 0, 0, 0, 88830, 0, 0, 0, 'Маг Бездны из клана Призрачной Луны to Телуунский стражник');
INSERT INTO `creature_text` VALUES (80167, 3, 0, 'МОИ ВЕЩИ!', 12, 0, 100, 0, 0, 0, 82265, 14000, 14000, 0, 'Груг to Player');
INSERT INTO `creature_text` VALUES (80172, 1, 0, 'Это мое барахло!', 12, 0, 100, 0, 0, 0, 82254, 0, 0, 0, 'Налетчик из клана Камнерогов to 61619');
INSERT INTO `creature_text` VALUES (80172, 3, 0, 'Вы Камнерогам и в подметки не годитесь!', 12, 0, 100, 0, 0, 0, 82256, 345000, 345000, 0, 'Налетчик из клана Камнерогов to Player');
