INSERT INTO `creature_text` VALUES (80182, 0, 0, 'Come, $p. I will introduce you to a friend of mine who can help you with your tower.', 12, 0, 100, 1, 0, 44904, 82109, 0, 0, 0, 'Archmage Khadgar to Player');
