INSERT INTO `creature_text` VALUES (80183, 1, 0, 'Very well, Archmage. As you wish.', 12, 0, 100, 1, 0, 45994, 82126, 0, 0, 0, 'Kirin Tor Gatekeeper to Player');
INSERT INTO `creature_text` VALUES (80184, 3, 0, 'Когда увидишь Газмольфа, скажи, что тебя прислала Габби!', 12, 0, 100, 0, 0, 0, 84638, 16266, 16266, 0, 'Габби Золотохват to Player');
