INSERT INTO `creature_text` VALUES (80185, 1, 0, 'Ah, there you are! This is the one I was telling you about.', 12, 0, 100, 1, 0, 44910, 82160, 0, 0, 0, 'Archmage Khadgar to Player');
INSERT INTO `creature_text` VALUES (80185, 2, 0, 'Very good. I\'m sure I\'ll be seeing the both of you soon.', 12, 0, 100, 1, 0, 44903, 82165, 0, 0, 0, 'Archmage Khadgar to Player');
INSERT INTO `creature_text` VALUES (80191, 0, 0, 'Лок\'тар огар!', 14, 0, 100, 36, 0, 0, 127481, 0, 0, 0, 'Юный мастер клинка to Житель Галлвалора');
