INSERT INTO `creature_text` VALUES (80191, 0, 0, 'Я докажу свою силу, убив тебя!', 14, 0, 100, 0, 0, 0, 82424, 0, 0, 0, 'Юный мастер клинка to Житель Галлвалора');
INSERT INTO `creature_text` VALUES (80191, 1, 0, 'Твои фокусы на меня не подействуют, |3-6($c).', 14, 0, 100, 0, 0, 0, 82426, 0, 0, 0, 'Юный мастер клинка to Житель Галлвалора');
INSERT INTO `creature_text` VALUES (80191, 1, 0, 'Твоя презренная кровь оросит мой клинок.', 14, 0, 100, 54, 0, 0, 82422, 0, 0, 0, 'Юный мастер клинка to Житель Галлвалора');
INSERT INTO `creature_text` VALUES (80191, 3, 0, 'Когда ты сдохнешь, о тебе никто и не вспомнит.', 14, 0, 100, 37, 0, 0, 82428, 458000, 458000, 0, 'Юный мастер клинка to Житель Галлвалора');
INSERT INTO `creature_text` VALUES (80191, 3, 0, 'Ты не знаешь, что такое честь.', 14, 0, 100, 0, 0, 0, 82425, 8593, 8593, 166782, 'Юный мастер клинка to Житель Галлвалора');
