INSERT INTO `creature_text` VALUES (80191, 4, 0, 'Ты заплатишь за вторжение своей жизнью!', 14, 0, 100, 0, 0, 0, 82427, 10243, 10243, 0, 'Юный мастер клинка to Житель Галлвалора');
INSERT INTO `creature_text` VALUES (80193, 1, 0, 'Странно, что весь этот шум не пробудил древних защитников. ', 12, 0, 100, 1, 0, 45503, 82347, 0, 0, 161822, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80193, 3, 0, 'I will go to your outpost to draw a scroll. We’ll meet there.', 12, 0, 100, 1, 0, 45513, 82351, 41496, 41496, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80193, 3, 0, 'Спалим все дотла!', 12, 0, 100, 1, 0, 45504, 82183, 1809, 1809, 163460, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80193, 3, 0, 'Хм. Возможно, нам пригодятся эти заряженные грибы.', 12, 0, 100, 1, 0, 45502, 82195, 47144, 47144, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80193, 4, 0, 'Кадгар высоко ценит тебя. Твой список подвигов впечатляет!', 12, 0, 100, 1, 0, 45514, 82217, 9282, 9282, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80193, 4, 0, 'Не думал, что чародейская энергия столь... мощная.', 12, 0, 100, 1, 0, 12819, 82219, 24149, 24149, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Бешеные элементали угрожают безопасности работников! Такого не было в контракте!', 12, 0, 100, 0, 0, 0, 82188, 174000, 174000, 0, 'Бригадир Тазвикс');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Вас обманули! Ограбили! Облапошили. Объегорили, в конце концов!', 12, 0, 100, 0, 0, 0, 82194, 12329000, 12329000, 0, 'Бригадир Тазвикс');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Если Прохлаждайсу нужны раскопки в ущелье Каменных Скал, пусть сам там и копает!', 12, 0, 100, 0, 0, 0, 82189, 9108000, 9108000, 0, 'Бригадир Тазвикс');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Зарплату выше! Больше отдыха! Меньше правил!', 12, 0, 100, 0, 0, 0, 82191, 10563, 10563, 0, 'Бригадир Тазвикс');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Когда в последний раз Газмольф разрешал нам устроить пирушку? Мы будем бороться за право повеселиться!', 12, 0, 100, 0, 0, 0, 82193, 25000, 25000, 0, 'Бригадир Тазвикс');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Нам давно пора заключить новый контракт! Пересмотреть условия труда!', 12, 0, 100, 0, 0, 0, 82190, 12161000, 12161000, 0, 'Бригадир Тазвикс');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Ну все, ребята, мы объявляем забастовку!', 12, 0, 100, 0, 0, 0, 82186, 161000, 802000, 0, 'Бригадир Тазвикс');
INSERT INTO `creature_text` VALUES (80195, 3, 0, 'Ну же! Пошумим! Пусть старый хрыч Прохлаждайс знает, что мы не уступим, пока не получим больше денег!', 12, 0, 100, 0, 0, 0, 82192, 18000, 18000, 0, 'Бригадир Тазвикс');
