INSERT INTO `creature_text` VALUES (80368, 1, 0, 'Лантрезор сплевывает и бросает свое знамя на землю.', 16, 0, 100, 61, 0, 964, 82516, 0, 0, 0, 'Лантрезор Клинок to Player');
INSERT INTO `creature_text` VALUES (80368, 2, 0, 'Ты ошибаешься. Это клан Пылающего Клинка недостоин меня!', 12, 0, 100, 0, 0, 0, 83376, 0, 0, 0, 'Лантрезор Клинок to Player');
INSERT INTO `creature_text` VALUES (80373, 0, 0, 'Клан Пылающего Клинка, внимайте мне! По праву преемственности я, Азука Ярость Клинка, дочь Дхарла, принимаю на себя бремя вождя.', 14, 0, 100, 22, 0, 0, 82511, 0, 0, 0, 'Азука Ярость Клинка to Player');
INSERT INTO `creature_text` VALUES (80373, 1, 0, 'Любой член клана, который захочет бросить мне вызов, вправе сделать это.', 14, 0, 100, 15, 0, 0, 82513, 0, 0, 0, 'Азука Ярость Клинка to Player');
INSERT INTO `creature_text` VALUES (80373, 2, 0, '$pc, ты можешь идти. Однако если мы встретимся снова, ты умрешь.', 14, 0, 100, 273, 0, 0, 82512, 0, 0, 0, 'Азука Ярость Клинка to Player');
INSERT INTO `creature_text` VALUES (80373, 3, 0, 'А ты, Лантрезор Клинок, не имел права участвовать в этой битве.', 14, 0, 100, 1, 0, 0, 82514, 0, 0, 0, 'Азука Ярость Клинка to Player');
INSERT INTO `creature_text` VALUES (80373, 4, 0, 'Ты – полукровка и изгой. Ты недостоин места в клане Пылающего Клинка. Уходи и больше не возвращайся.', 14, 0, 100, 25, 0, 0, 82515, 0, 0, 0, 'Азука Ярость Клинка to Player');
INSERT INTO `creature_text` VALUES (80374, 1, 0, 'Земля сокрушит твои кости!', 12, 0, 100, 0, 0, 0, 90482, 0, 0, 0, 'Нестойкий страж земли to Player');
INSERT INTO `creature_text` VALUES (80374, 3, 0, 'Гордауга нигде не видно! Это вина прислужников Каландриоса. Наполни воздух земляной пылью… Пусть ветер задохнется!', 14, 0, 100, 53, 0, 0, 90458, 38000, 38000, 0, 'Нестойкий страж земли');
INSERT INTO `creature_text` VALUES (80375, 1, 0, 'Как ты смеешь приближаться к ярости бури?!', 12, 0, 100, 53, 0, 0, 90483, 0, 87000, 0, 'Оскверненный страж ветра to Player');
INSERT INTO `creature_text` VALUES (80375, 3, 0, 'Великий дух земли покинул трон! Прислужники Гордауга должны понести наказание… Развей землю по ветру!', 14, 0, 100, 53, 0, 0, 90459, 1000, 60000, 0, 'Оскверненный страж ветра');
INSERT INTO `creature_text` VALUES (80376, 1, 0, 'Мой огонь превратит твои кости в пепел!', 12, 0, 100, 0, 0, 0, 90488, 0, 0, 0, 'Изменчивый страж пламени to Player');
INSERT INTO `creature_text` VALUES (80376, 3, 0, 'The fury of the earth has disappeared, and the aborious minions do not help! If the water refuses to flow ... we will make it boil!', 14, 0, 100, 53, 0, 0, 90460, 1000, 1000, 178451, 'Изменчивый страж пламени');
INSERT INTO `creature_text` VALUES (80377, 1, 0, 'Мои воды очистят твою жалкую плоть!', 12, 0, 100, 0, 0, 0, 90486, 0, 0, 0, 'Запятнанный страж воды to Player');
INSERT INTO `creature_text` VALUES (80377, 3, 0, 'Прислужники Гордауга неуправляемы! Слуги Возжигателя горят! Очисти плато… залей стихии водой и смой их волнами!', 14, 0, 100, 53, 0, 0, 90461, 1000, 58000, 0, 'Запятнанный страж воды');
INSERT INTO `creature_text` VALUES (80378, 3, 0, 'На болотах ты найдешь скрывшихся рангари. Поговори с каждым из них и возвращайся.', 12, 0, 100, 0, 0, 0, 82674, 12000, 12000, 0, 'Рангари Навра to Player');
INSERT INTO `creature_text` VALUES (80378, 3, 0, 'Поразительно. Благодарю за столь скорое появление здесь, $n.', 12, 0, 100, 0, 0, 0, 82675, 614000, 614000, 0, 'Рангари Навра to Player');
INSERT INTO `creature_text` VALUES (80387, 0, 0, 'We Rangari are trained to endure long vigils. I have not moved a single muscle for several hours.', 12, 0, 100, 0, 0, 0, 82669, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (80387, 0, 1, 'Hydras are causing quite a bit of chaos. We will eventually need to take care of them to ensure our safety.', 12, 0, 100, 0, 0, 0, 82673, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (80387, 0, 2, 'The swamplighters seem especially upset tonight.', 12, 0, 100, 0, 0, 0, 82670, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (80387, 0, 3, 'Granny Loola needs to be more careful. She has been wandering around the marsh kissing toads again.', 12, 0, 100, 0, 0, 0, 82672, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (80389, 3, 0, 'I will send us back.', 12, 0, 100, 1, 0, 45491, 82844, 883000, 883000, 32615, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 3, 0, 'Войска готовы к телепортации. Моркурку не выстоять против нас.', 12, 0, 100, 1, 0, 45497, 82680, 245000, 245000, 162463, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 3, 0, 'Твои войска разберутся с оставшимися ограми. Уходим.', 12, 0, 100, 1, 0, 45493, 82731, 878000, 878000, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 3, 0, 'Это мне по нраву.', 12, 0, 100, 1, 0, 45505, 87926, 1000, 1000, 163460, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 4, 0, 'Классно сражаешься! Теперь осталось только разобраться с Воргрошем и Кромаугом.', 12, 0, 100, 4, 0, 0, 82653, 5000, 5000, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 4, 0, 'Классно сражаешься! Теперь осталось только разобраться с Зулморком и Кромаугом.', 12, 0, 100, 4, 0, 0, 82650, 4000, 4000, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 4, 0, 'Мы расправились с ними. Теперь они нам не помешают.', 12, 0, 100, 4, 0, 45509, 82663, 10000, 10000, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 4, 0, 'Прежде чем использовать наш свиток массовой телепортации, нужно уничтожить астральных стражей.', 12, 0, 100, 1, 0, 45496, 82644, 6000, 6000, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 4, 0, 'Прекрасно. Берем кристалл и уходим!', 12, 0, 100, 1, 0, 45492, 82825, 55000, 55000, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80389, 4, 0, 'Теперь мы найдем Кромауга и прервем его никчемную жизнь.', 12, 0, 100, 4, 0, 0, 82654, 8000, 8000, 161991, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80390, 3, 0, 'На чем мы остановились?', 12, 0, 100, 6, 0, 45499, 82691, 12000, 12000, 0, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80400, 0, 0, 'Вам не добраться до Железного цеха!', 12, 0, 100, 0, 0, 0, 82772, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (80409, 0, 0, 'Эй! Взять их!', 12, 0, 100, 0, 0, 0, 82861, 0, 0, 0, 'Марког Аба\'дир to 0');
INSERT INTO `creature_text` VALUES (80409, 1, 0, 'Ар-р-р!', 12, 0, 100, 0, 0, 0, 82862, 0, 0, 0, 'Марког Аба\'дир to 0');
INSERT INTO `creature_text` VALUES (80412, 0, 0, 'Видимо, Гордунни давно утратили связь с империей. Они правда верят, что стена нас остановит?', 12, 0, 100, 1, 0, 45498, 82563, 0, 0, 0, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80412, 1, 0, 'Вот так! Ну что, вперед?', 12, 0, 100, 1, 0, 45499, 82564, 0, 0, 0, 'Магистр Крелас to Player');
INSERT INTO `creature_text` VALUES (80419, 4, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 38630, 7109, 7109, 18501, 'Frostfire Gronnling');
INSERT INTO `creature_text` VALUES (80419, 4, 0, '%s is furious!', 16, 0, 100, 0, 0, 0, 38630, 2000, 2000, 0, 'Малый гронн Ледяного Огня');
INSERT INTO `creature_text` VALUES (80423, 4, 0, 'Шевелись, животные! Припасы нужно перетащить вниз!', 12, 0, 100, 5, 0, 0, 82771, 7469, 7469, 162633, 'Звериный сторож Громоборцев');
INSERT INTO `creature_text` VALUES (80429, 0, 0, 'Рада тебе, командир. Надо бы сейчас благословить моих мертвецов, вырубить щит и вырезать всех этих из Песни Войны.', 12, 0, 100, 273, 0, 0, 82717, 0, 0, 0, 'Темная охотница Каджасса to Player');
INSERT INTO `creature_text` VALUES (80429, 1, 0, 'Щит включился. Пора нанести удар, $n. Убей Каргору и отключи щит.', 12, 0, 100, 5, 0, 0, 82758, 0, 0, 0, 'Темная охотница Каджасса to Player');
INSERT INTO `creature_text` VALUES (80430, 3, 0, 'Ни за что. Малрок сдерет шкуру с нас обоих, если я подпущу кого-нибудь к этой штуке.', 12, 0, 100, 274, 0, 0, 82884, 2000, 2000, 0, 'Живодер из клана Изувеченной Длани to Головорез из клана Изувеченной Длани');
INSERT INTO `creature_text` VALUES (80434, 0, 0, 'Огонь и шипы углецвета всегда напоминали мне о ее силе и решимости.', 12, 0, 100, 1, 0, 0, 82892, 0, 0, 0, 'Гар\'рок to Player');
INSERT INTO `creature_text` VALUES (80434, 1, 0, 'Я всегда подозревал Чалли, но у меня не было доказательств, чтобы предать ее суду.', 12, 0, 100, 1, 0, 0, 82893, 0, 0, 0, 'Гар\'рок to Player');
INSERT INTO `creature_text` VALUES (80434, 2, 0, 'Мое время на исходе, но я умираю, зная, что почтил ее память.', 12, 0, 100, 0, 0, 0, 82894, 0, 0, 0, 'Гар\'рок to Player');
INSERT INTO `creature_text` VALUES (80441, 3, 0, 'Не-а, так не пойдет.', 12, 0, 100, 0, 0, 0, 82640, 149000, 149000, 162473, 'Эксперт по взрывчатым веществам to 231111');
INSERT INTO `creature_text` VALUES (80441, 3, 0, 'Хм... Вот так нормально.', 12, 0, 100, 0, 0, 0, 82639, 240000, 372000, 162473, 'Эксперт по взрывчатым веществам to 231111');
INSERT INTO `creature_text` VALUES (80442, 3, 0, 'А когда перерыв на пиво?', 12, 0, 100, 0, 0, 0, 82665, 198000, 198000, 0, 'Землекоп to 231110');
INSERT INTO `creature_text` VALUES (80442, 3, 0, 'Здесь очень разреженный воздух...', 12, 0, 100, 0, 0, 0, 82661, 258000, 258000, 0, 'Землекоп to 231110');
INSERT INTO `creature_text` VALUES (80442, 3, 0, 'Из чего эта кирка сделана? Из магнита?', 12, 0, 100, 0, 0, 0, 82666, 275000, 385000, 0, 'Землекоп to 231110');
INSERT INTO `creature_text` VALUES (80442, 3, 0, 'Кому кафу? 10 золотых за один боб.', 12, 0, 100, 0, 0, 0, 82668, 195000, 195000, 0, 'Землекоп to 231110');
INSERT INTO `creature_text` VALUES (80442, 3, 0, 'Нужно позвать на помощь батраков.', 12, 0, 100, 0, 0, 0, 82664, 463000, 463000, 0, 'Землекоп to 231110');
INSERT INTO `creature_text` VALUES (80442, 3, 0, 'Чем упорнее работаем, тем ближе профит.', 12, 0, 100, 0, 0, 0, 82662, 198000, 198000, 0, 'Землекоп to 231110');
INSERT INTO `creature_text` VALUES (80443, 3, 0, 'Парни, не спать на работе!', 12, 0, 100, 0, 0, 0, 82637, 152000, 152000, 0, 'Штейгер to 231115');
INSERT INTO `creature_text` VALUES (80451, 0, 0, 'А ведь там все проще некуда. Надо просто активировать кристаллы по периметру. Хорошо, что клан Песни Войны не отличается умом и сообразительностью.', 12, 0, 100, 0, 0, 0, 82718, 0, 0, 0, 'Рангари Кейлней to Player');
INSERT INTO `creature_text` VALUES (80451, 1, 0, 'Кейлней приободрилась.', 16, 0, 100, 0, 0, 0, 82760, 0, 0, 0, 'Рангари Кейлней to Player');
INSERT INTO `creature_text` VALUES (80454, 3, 0, 'Hey honey, I\'m back.', 12, 0, 100, 70, 0, 0, 82646, 2594, 2594, 0, 'Vulaa to Player');
INSERT INTO `creature_text` VALUES (80454, 3, 0, 'Oh not again... Loola, I\'m right here. I\'m not a toad... for Light\'s sake.', 12, 0, 100, 20, 0, 0, 82647, 7063, 7063, 0, 'Vulaa to Player');
INSERT INTO `creature_text` VALUES (80454, 3, 0, 'Любимая, а вот и я.', 12, 0, 100, 70, 0, 0, 82646, 2000, 2000, 0, 'Вулаа to Player');
INSERT INTO `creature_text` VALUES (80454, 3, 0, 'О нет, только не это... Лоола, я здесь. Я не жаба... разрази меня Свет.', 12, 0, 100, 20, 0, 0, 82647, 7000, 7000, 0, 'Вулаа to Player');
INSERT INTO `creature_text` VALUES (80481, 0, 0, 'Слава Прародительнице воронов, у нас все готово! Поговори со мной еще раз, когда будешь $gготов:готова; начинать.', 12, 0, 100, 0, 0, 0, 85718, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (80483, 0, 0, 'I will swallow you!', 14, 0, 100, 0, 0, 0, 82707, 0, 0, 0, 'Чалли to 0');
INSERT INTO `creature_text` VALUES (80483, 1, 0, 'Трепещи перед моей истинной силой!', 14, 0, 100, 0, 0, 0, 82708, 0, 0, 0, 'Чалли to 0');
INSERT INTO `creature_text` VALUES (80483, 4, 0, 'Значит, несчастный дуралей Гар\'рок умирает – и хочет узнать правду?', 12, 0, 100, 0, 0, 0, 82709, 0, 0, 0, 'Чалли to Player');
INSERT INTO `creature_text` VALUES (80483, 5, 0, 'I remember Dahak perfectly. She was like I am now ...', 12, 0, 100, 0, 0, 0, 82710, 0, 0, 0, 'Чалли to Player');
INSERT INTO `creature_text` VALUES (80483, 6, 0, '...пока я не выкачала всю энергию из ее жалкого тельца!', 12, 0, 100, 0, 0, 0, 82713, 0, 0, 0, 'Чалли to Player');
INSERT INTO `creature_text` VALUES (80483, 7, 0, 'Гар\'рок – глупец! А ведь я бы поделилась с ним силой!', 14, 0, 100, 0, 0, 28232, 82706, 0, 0, 0, 'Чалли to 0');
INSERT INTO `creature_text` VALUES (80484, 3, 0, 'I will not fail. I\'ll be back after training.', 14, 0, 100, 0, 0, 0, 86674, 33063, 33063, 0, 'Джексон Баджира to Джексон Баджира');
INSERT INTO `creature_text` VALUES (80484, 3, 0, 'Гнев Баджиры вырвался на волю!', 14, 0, 100, 0, 0, 0, 86675, 1000, 1000, 178000, 'Джексон Баджира to Джексон Баджира');
INSERT INTO `creature_text` VALUES (80484, 4, 0, 'В лепешку!', 14, 0, 100, 0, 0, 0, 108140, 9000, 9000, 164461, 'Джексон Баджира to Шеф-повар Джейсон Квинн');
INSERT INTO `creature_text` VALUES (80484, 4, 0, 'Врезал как надо!', 14, 0, 100, 0, 0, 0, 86671, 20352, 20352, 0, 'Джексон Баджира to Player');
INSERT INTO `creature_text` VALUES (80485, 2, 0, 'I am not afraid of defeat.', 14, 0, 100, 0, 0, 46573, 82736, 0, 0, 6136, 'Шани Фризвинд to Шани Фризвинд');
INSERT INTO `creature_text` VALUES (80485, 3, 0, 'Пришла пора узнать себе цену, оценить предел своих возможностей и преодолеть его!', 14, 0, 100, 0, 0, 0, 82735, 2000, 2000, 178000, 'Шани Фризвинд to Шани Фризвинд');
INSERT INTO `creature_text` VALUES (80486, 2, 0, 'Побежден? Нет! Чарльз, где ты?', 14, 0, 100, 0, 0, 0, 90395, 0, 0, 178071, 'Некролорд Азаэль to Некролорд Азаэль');
INSERT INTO `creature_text` VALUES (80486, 3, 0, 'Мою силу превосходит только мой рост!', 14, 0, 100, 0, 0, 0, 90389, 1000, 1000, 178002, 'Некролорд Азаэль to Некролорд Азаэль');
INSERT INTO `creature_text` VALUES (80486, 4, 0, 'Живой или мертвый, я все равно выше тебя!', 14, 0, 100, 0, 0, 46573, 90397, 3000, 3000, 178071, 'Некролорд Азаэль to Гадюка');
INSERT INTO `creature_text` VALUES (80486, 4, 0, 'Меня не остановить!', 14, 0, 100, 0, 0, 46573, 116784, 3000, 3000, 178071, 'Некролорд Азаэль to Гадюка');
INSERT INTO `creature_text` VALUES (80488, 2, 0, 'Я вернусь и буду сильнее, чем прежде!', 14, 0, 100, 0, 0, 0, 90403, 0, 0, 0, 'Алана Ветряная Шкура to Алана Ветряная Шкура');
INSERT INTO `creature_text` VALUES (80488, 3, 0, 'Сила солнца и луны течет сквозь меня, оглушительно ухая, звонко звеня.', 14, 0, 100, 0, 0, 49299, 90401, 1000, 1000, 178000, 'Алана Ветряная Шкура to Алана Ветряная Шкура');
INSERT INTO `creature_text` VALUES (80488, 4, 0, 'БУМ!', 14, 0, 100, 0, 0, 0, 131154, 50014, 50014, 162622, 'Алана Ветряная Шкура to 58456');
INSERT INTO `creature_text` VALUES (80488, 4, 0, 'ТЫДЫЖ!', 14, 0, 100, 0, 0, 0, 90409, 19000, 19000, 162623, 'Алана Ветряная Шкура to Войско мертвых');
INSERT INTO `creature_text` VALUES (80489, 0, 0, 'Невыгодное в итоге оказалось дело...', 14, 0, 100, 0, 0, 0, 90714, 0, 0, 0, 'Душевар Надагаст to Душевар Надагаст');
INSERT INTO `creature_text` VALUES (80489, 1, 0, 'Ну что, мальчики и девочки, пришло время повеселиться!', 14, 0, 100, 0, 0, 0, 90709, 0, 0, 178084, 'Душевар Надагаст to Душевар Надагаст');
INSERT INTO `creature_text` VALUES (80489, 4, 0, 'Вкусненько!', 14, 0, 100, 0, 0, 0, 90906, 59214, 59214, 0, 'Душевар Надагаст to Пехотинец Преграды Ветров');
INSERT INTO `creature_text` VALUES (80489, 4, 0, 'Порубили и сварили.', 14, 0, 100, 0, 0, 0, 90708, 31000, 31000, 0, 'Душевар Надагаст to Страж Скверны');
INSERT INTO `creature_text` VALUES (80490, 1, 0, 'Вы готовы сразиться с величайшим воителем Азерота?!', 14, 0, 100, 0, 0, 49299, 83610, 0, 0, 178086, 'Подчиняющий разум Талдабар to Подчиняющий разум Талдабар');
INSERT INTO `creature_text` VALUES (80490, 2, 0, 'Мое возвращение станет твоей гибелью.', 14, 0, 100, 0, 0, 0, 83607, 0, 0, 177402, 'Подчиняющий разум Талбадар to Подчиняющий разум Талбадар');
INSERT INTO `creature_text` VALUES (80491, 3, 0, 'Неумолимая мощь стихий сломит тебя!', 14, 0, 100, 0, 0, 0, 90965, 1508, 1508, 178002, 'Элементалист Ново to Элементалист Ново');
INSERT INTO `creature_text` VALUES (80491, 4, 0, 'Земля примет меня, и я восстану в огне!', 14, 0, 100, 0, 0, 0, 90967, 38052, 38052, 0, 'Элементалист Ново to Элементалист Ново');
INSERT INTO `creature_text` VALUES (80491, 4, 0, 'Меня не остановить!', 14, 0, 100, 0, 0, 0, 116784, 10000, 10000, 178059, 'Элементалист Ново to 86183');
INSERT INTO `creature_text` VALUES (80491, 4, 0, 'Назад в землю, дворняга!', 14, 0, 100, 0, 0, 8332, 91068, 12803, 12803, 0, 'Элементалист Ново to Пехотинец Преграды Ветров');
