INSERT INTO `creature_text` VALUES (80864, 0, 0, 'Если не зажечь костры, пустота скоро поглотит и нас.', 12, 0, 100, 1, 0, 0, 83037, 0, 0, 0, 'Гар\'рок to Player');
INSERT INTO `creature_text` VALUES (80864, 1, 0, 'Дух Голмаша не знает жалости. Пусть этот дар древних ветров направит твою руку.', 12, 0, 100, 51, 0, 0, 83036, 0, 0, 0, 'Гар\'рок to Player');
