INSERT INTO `creature_text` VALUES (80865, 1, 0, 'Этим растениям повезло, что при мне не оказалось любимых ножниц. В следующий раз я хорошенько пройдусь по их… почкам.', 12, 0, 100, 0, 0, 0, 83317, 0, 0, 0, 'Таренар Удар Солнца to Player');
INSERT INTO `creature_text` VALUES (80866, 0, 0, 'Отголоски должны умолкнуть, иначе оставшиеся духи так и не обретут покоя.', 12, 0, 100, 1, 0, 0, 83038, 0, 0, 0, 'Дахака to Player');
INSERT INTO `creature_text` VALUES (80869, 0, 0, 'Где твоя вера, начертатель?! Что сказано в Свитке Темного Рассвета? \"Все вороны стаи должны держаться вместе!\" Когда Прародительница воронов и все ее дети вернутся в гнездо, мы будем в полной безопасности!', 12, 0, 100, 0, 0, 0, 82989, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (80871, 0, 0, 'Разрази меня Прародительница воронов! Как мы попали в такую передрягу?', 12, 0, 100, 0, 0, 0, 82988, 0, 0, 0, 'Вакора из Стаи to Player');
