INSERT INTO `creature_text` VALUES (80879, 0, 0, 'Идем. Я отведу тебя прямо к Верховной Говорящей с воронами, да.', 12, 0, 100, 0, 0, 0, 82997, 0, 0, 0, 'Иктис из Стаи to Player');
INSERT INTO `creature_text` VALUES (80879, 1, 0, 'Ты $gпришел:пришла; к нам очень вовремя, да. Скоро сама Прародительница воронов присоединится к нам!', 12, 0, 100, 0, 0, 0, 83000, 0, 0, 0, 'Иктис из Стаи to Player');
INSERT INTO `creature_text` VALUES (80879, 2, 0, 'Верховная Говорящая с воронами, $gэтот иноземец пришел:эта чужестранка пришла; к нам, как и было сказано в писании! Да.', 12, 0, 100, 0, 0, 0, 83002, 0, 0, 0, 'Иктис из Стаи to Player');
