INSERT INTO `creature_text` VALUES (81770, 5, 0, 'And that brings us to today! With the relics and the Talonpriests in place, we are ready to call upon the Talon King.', 12, 0, 100, 0, 0, 46053, 85301, 0, 0, 0, 'Reshad to Player');
